    width: 82px;
    height: 25px;
    object-fit: contain;
    filter: brightness(0) invert(1);
}

/* Date segment */
.date-segment {
    position: absolute;
    left: 168px; /* x=537px in spec, relative to nav bar at x=369px = 168px */
    width: 134px;
}

/* Creditor segment */
.creditor-segment {
    position: absolute;
    left: 375px;
    width: 150px;
}

.creditor-segment .nav-text-container {
    max-width: 150px;
    overflow: hidden;
}

/* Page segment */
.page-segment {
    position: absolute;
    left: 600px;
    width: 62px;
}

/* Evidence Navigation Segments */
.category-segment {
    position: absolute;
    left: 20px;
    width: 180px;
}

.document-segment {
    position: absolute;
    left: 280px;
    width: 280px;
