}

/* Individual severity box */
.severity-box {
    width: 123px;
    height: 98px;
    background-color: #e3e2e1;
    border: 1px solid white;
    border-radius: 32px;
    box-shadow: 1px 3px 4px rgba(0, 0, 0, 0.08);
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
}

/* Severity label */
.severity-label {
    position: absolute;
    top: 6px;
    width: 110px;
    height: 34px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 9.6pt;
    color: rgba(37, 53, 65, 0.9);
