}

.toggle-indicator.report {
    left: 90px;
}

.toggle-indicator.evidence {
    left: 180px;
}

/* All legacy navigation styles removed â€” see legacy-removal.html */

/* ========================================
   Main Content Area
   ======================================== */
.main-content {
    position: relative;
    background: transparent;
    width: 100%;
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
