    padding: 6px 12px;
    border-radius: 18px;
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.2s ease, visibility 0.2s ease;
    transition-delay: 0.15s;
    pointer-events: none;
    z-index: 10000;
}

.nav-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 4px solid transparent;
    border-top-color: #f26419;
}

/* Show tooltips only when tips are enabled and on hover */
.tooltip-mode .nav-arrow-small:hover .nav-tooltip {
    opacity: 1;
    visibility: visible;
}

.report-canvas,
.tagger-canvas,
.evidence-canvas {
    width: 100%;
    height: 100%;
    position: relative;
    background-color: #ffffff;
    overflow: hidden;
    z-index: 1;
    border-radius: 24px; /* Match container border-radius */
}

/* Canvas placeholder for empty states */
.canvas-placeholder {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100%;
