    font-weight: 700;
    margin-bottom: 16px;
    color: #253541;
}

.canvas-placeholder p {
    font-size: 18px;
    font-weight: 400;
    color: #9ba1a6;
}

/* Canvas and sidebar are now fixed position, no flex needed */

.report-image {
    width: 100%;
    height: auto;
    max-height: 100%;
