}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    border: 2px solid rgba(203, 213, 225, 0.9); /* viobox-inactive with 0.9 opacity */
    border-radius: 13px;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    padding-left: 8px; /* Move content 8px from left */
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
    z-index: 4;
    box-sizing: border-box; /* Include border in width/height */
}

.viobox:hover {
    border-color: rgba(0, 102, 255, 0.9); /* viobox-active with 0.9 opacity */
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border-color: #253541;
    background-color: rgba(0, 102, 255, 0.1);
    outline: 2px solid #253541;
    outline-offset: -2px;
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 19.2px; /* 20% smaller: 24px * 0.8 */
    height: 19.2px; /* 20% smaller: 24px * 0.8 */
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: none;
    margin-left: -12px; /* Move 1px more left to align with border (-11px - 1px) */
    padding: 3px; /* Padding set to 3px */
    box-sizing: border-box; /* Include padding in width/height */
    position: relative;
    z-index: 10; /* Above VIObox borders */
}

.severity-icon i {
    width: 100%; /* Fill available space after padding */
    height: 100%; /* Fill available space after padding */
    color: #ffffff;
}

/* Dot styles - circles with no stroke */
.severity-high {
    background-color: #ff0000;
}

.severity-medium {
    background-color: #f26419;
}

.severity-low {
    background-color: #f2b919;
}

