}

.violation-details-toggle-collapsed:hover svg {
    stroke: #F26419 !important; /* Orange on hover */
}

.violation-details-toggle-collapsed:hover [data-lucide] {
    stroke: #F26419 !important;
}

/* Show/hide buttons based on state */
