/* Intro text */
.filter-counter-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 8.5pt;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.2;
    padding: 6px 24px 4px 24px; /* Reduced top padding by 25% (8px to 6px) */
