.tips-toggle[aria-pressed="true"] {
    background-color: var(--color-primary);
}

.tips-toggle i {
    width: 16px;
    height: 16px;
}

/* Violation Counter */
.violation-counter {
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
    font-size: 20px;
    font-weight: var(--font-bold);
}

.violation-counter .count {
    color: var(--color-primary);
}

/* ========================================
   Main Content Area
   ======================================== */
.main-content {
    flex: 1;
    display: flex;
    overflow: hidden;
}

.view-panel {
    width: 100%;
    display: none;
}

.view-panel.active {
    display: block;
}

.content-wrapper {
    display: flex;
    height: 100%;
}

/* ========================================
