}

.page-separator, .page-total {
    font-size: 20px;
    font-weight: var(--font-regular);
    color: var(--text-secondary);
}

/* Tips Toggle */
.tips-toggle {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 16px;
    background-color: var(--text-secondary);
    color: var(--text-white);
