}

.violation-severity {
    font-size: 14px;
    font-weight: var(--font-medium);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--text-secondary);
}

.violation-description {
    font-size: 16px;
    font-weight: var(--font-regular);
    color: var(--text-primary);
