    position: absolute;
    top: 50%;
    transform: translateY(-50%);
}

/* Labels and values in same container */
.nav-text-container {
    display: flex;
    align-items: baseline;
    gap: 4px;
    height: 17px;
}

.nav-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #9ba1a6;
    text-transform: lowercase;
}

.nav-value {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #ffffff;
    text-transform: lowercase;
}

/* Arrows positioned side-by-side */
.nav-arrows {
    position: absolute;
    display: flex;
    gap: 5px;
    align-items: center;
}

/* Specific arrow positioning for each segment */
.bureau-segment .nav-arrows {
    left: 81px;
}

.date-segment .nav-arrows {
    left: 137px;
}

.creditor-segment .nav-arrows {
    left: 154px;
}

.page-segment .nav-arrows {
    left: 64px;
}

/* Updated arrow button styles */
.nav-arrow-small {
    width: 17px;
    height: 17px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
    border: none;
    color: #9ba1a6;
    transition: all var(--transition-fast);
