    padding: 0 24px;
    align-items: center;
    justify-content: space-between;
    border-bottom: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-sm);
    position: relative;
}

.nav-left, .nav-center, .nav-right {
    display: flex;
