    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: var(--text-primary);
}

.tooltip-mode .tips-toggle:hover .tooltip {
    opacity: 1;
}

/* VIObox Tooltips */
.viobox-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
