.view-btn.active {
    background-color: var(--color-primary);
    color: var(--text-white);
}

/* Creditor Display */
.creditor-display {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 8px 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
}

