    border: 1px solid var(--border-subtle);
}

.nav-arrow:hover {
    background-color: var(--color-primary);
    color: var(--text-white);
}

.nav-arrow:active {
    background-color: var(--color-primary-dark);
}

.page-input-group {
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
}

.page-input {
    width: 4ch;
    padding: 8px;
    font-size: 20px;
    font-family: var(--font-mono);
    font-weight: var(--font-medium);
    text-align: center;
    background-color: var(--bg-light);
    color: var(--text-dark);
    border: 2px solid transparent;
    border-radius: 12px;
    transition: border-color var(--transition-fast);
}

.page-input:focus {
    border-color: var(--color-primary);
    outline: none;
}

.page-separator, .page-total {
    font-size: 20px;
    font-weight: var(--font-regular);
    color: var(--text-secondary);
}

/* Tips Toggle */
.tips-toggle {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 16px;
    background-color: var(--text-secondary);
    color: var(--text-white);
    border-radius: 24px;
    font-size: 18px;
    font-weight: var(--font-medium);
    transition: all var(--transition-fast);
    min-height: 36px;
    position: relative;
}

.tips-toggle[aria-pressed="true"] {
    background-color: var(--color-primary);
}

.tips-toggle i {
    width: 16px;
    height: 16px;
}

/* Violation Counter */
.violation-counter {
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
    font-size: 20px;
    font-weight: var(--font-bold);
}

.violation-counter .count {
    color: var(--color-primary);
}

/* ========================================
   Main Content Area
   ======================================== */
.main-content {
    position: static;
    background: transparent;
    width: 100%;
    height: 100%;
}

.view-panel {
    width: 100%;
    display: none;
}

.view-panel.active {
    display: block;
}

.content-wrapper {
    position: static;
    display: block;
    width: 100%;
    height: 100%;
}

/* ========================================
   Report Canvas
   ======================================== */
.report-canvas-container {
    position: absolute;
    left: 360px;
    top: 129px;
    width: 810px;
    height: 920px;
    background-color: #ffffff;
    border-radius: 24px;
    padding: 0;
    box-shadow: none;
    overflow: hidden;
    z-index: 1;
}

/* Report Navigation Bar */
.report-nav-bar {
    position: absolute;
    left: 360px;
    top: 70px;
    width: 792px;
    height: 40px;
    background-color: #253541;
