    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
}

.page-input {
    width: 4ch;
    padding: 8px;
    font-size: 20px;
    font-family: var(--font-mono);
    font-weight: var(--font-medium);
    text-align: center;
    background-color: var(--bg-light);
    color: var(--text-dark);
    border: 2px solid transparent;
    border-radius: 12px;
    transition: border-color var(--transition-fast);
}

.page-input:focus {
    border-color: var(--color-primary);
    outline: none;
}

.page-separator, .page-total {
    font-size: 20px;
    font-weight: var(--font-regular);
    color: var(--text-secondary);
}

/* Tips Toggle */
.tips-toggle {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 16px;
    background-color: var(--text-secondary);
    color: var(--text-white);
    border-radius: 24px;
    font-size: 18px;
    font-weight: var(--font-medium);
    transition: all var(--transition-fast);
    min-height: 36px;
    position: relative;
}

.tips-toggle[aria-pressed="true"] {
