    left: 137px;
}

.creditor-segment .nav-arrows {
    left: 154px;
}

.page-segment .nav-arrows {
    left: 64px;
}

/* Updated arrow button styles */
.nav-arrow-small {
    width: 17px;
    height: 17px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
    border: none;
    color: #9ba1a6;
    transition: all var(--transition-fast);
    cursor: pointer;
    padding: 0;
    position: relative;
    z-index: 2;
}

.nav-arrow-small:hover {
    color: #f26419;
}

.nav-arrow-small i {
    width: 17px;
    height: 17px;
}

/* Vertical dividers */
.nav-divider {
    position: absolute;
    width: 1px;
    height: 18px;
    background-color: #9ba1a6;
    top: 50%;
    transform: translateY(-50%);
    z-index: 0;
}

.nav-divider:nth-of-type(1) {
    left: 151px;
}

.nav-divider:nth-of-type(2) {
    left: 359px;
}

.nav-divider:nth-of-type(3) {
    left: 583px;
}

.report-canvas {
    width: 100%;
    height: 100%;
    position: relative;
    background-color: #ffffff;
    overflow: hidden;
    z-index: 1;
}

.report-image {
    width: 100%;
    height: auto;
    max-height: 100%;
    object-fit: contain;
}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    width: 557px; /* 10% wider than 506px */
    height: 26px; /* 10% deeper than 24px */
    border: 2px solid var(--viobox-inactive);
    border-radius: 13px;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
    z-index: 4;
}

.viobox:hover {
    border-color: var(--viobox-active);
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border-color: var(--viobox-active);
    background-color: rgba(0, 102, 255, 0.1);
    box-shadow: 0 0 0 3px rgba(0, 102, 255, 0.2);
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 24px;
    height: 24px;
    border-radius: 6px;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-sm);
}

.severity-icon i {
    width: 16px;
    height: 16px;
    color: var(--text-white);
}

.severity-high {
    background-color: var(--severity-high-bg);
}

.severity-medium {
    background-color: var(--severity-medium-bg);
}

.severity-low {
    background-color: var(--severity-low-bg);
}

/* ========================================
   Violation Sidebar
   ======================================== */
.violation-sidebar, .tagger-sidebar, .evidence-context {
    display: block;
    width: var(--sidebar-width);
    background-color: var(--bg-card);
    border-left: 1px solid var(--border-subtle);
    padding: var(--content-padding);
    overflow-y: auto;
    box-shadow: -4px 0 6px rgba(0, 0, 0, 0.05);
}

.sidebar-heading {
    font-size: 24px;
    font-weight: var(--font-bold);
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-sm);
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-size: 14px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    font-weight: var(--font-light);
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-size: 20px;
    font-weight: var(--font-bold);
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

.violation-item {
    display: flex;
    align-items: flex-start;
    gap: 12px;
    padding: 12px;
    margin-bottom: 8px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    transition: all var(--transition-fast);
    border: 1px solid var(--border-subtle);
}

.violation-item:hover {
    background-color: var(--bg-hover);
    transform: translateX(4px);
    box-shadow: var(--shadow-sm);
}

.violation-item-icon {
    flex-shrink: 0;
}

.violation-item-text {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.violation-severity {
    font-size: 14px;
    font-weight: var(--font-medium);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--text-secondary);
}

.violation-description {
    font-size: 16px;
    font-weight: var(--font-regular);
    color: var(--text-primary);
    line-height: 1.4;
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-context {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
    }

    .viobox {
        width: 90%;
        max-width: 557px;
    }
}

/* ========================================
   Tooltip System
   ======================================== */
.tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 12px;
    background-color: var(--text-primary);
    color: var(--text-white);
    border-radius: 8px;
    font-size: 16px;
    font-weight: var(--font-regular);
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: var(--z-dropdown);
    box-shadow: var(--shadow-lg);
}

.tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: var(--text-primary);
}

.tooltip-mode .tips-toggle:hover .tooltip {
    opacity: 1;
}

/* VIObox Tooltips */
.viobox-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 16px;
    background-color: var(--text-primary);
    color: var(--text-white);
    border-radius: 8px;
    font-size: 14px;
    font-weight: var(--font-regular);
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: var(--z-dropdown);
    box-shadow: var(--shadow-lg);
}

.viobox-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: var(--text-primary);
}

.tooltip-mode .viobox:hover .viobox-tooltip {
    opacity: 1;
}

