166	    toggleTips() {
167	        this.tipsEnabled = !this.tipsEnabled;
168	        const toggle = document.querySelector('.tips-toggle');
169	        
170	        if (toggle) {
171	            toggle.setAttribute('aria-pressed', this.tipsEnabled);
172	            document.body.classList.toggle('tooltip-mode', this.tipsEnabled);
173	            
174	            // Update button text
175	            const span = toggle.querySelector('span');
176	            if (span) {
177	                span.textContent = this.tipsEnabled ? 'Tips ON' : 'Tips OFF';
178	            }
179	            
180	            // Update icon style
181	            const icon = toggle.querySelector('i');
182	            if (icon) {
183	                icon.style.fill = this.tipsEnabled ? 'currentColor' : 'none';
184	            }
