197	    goToPage(pageNum) {
198	        if (pageNum < 1 || pageNum > this.totalPages || isNaN(pageNum)) {
199	            return;
200	        }
201	        
202	        this.currentPage = pageNum;
203	        const pageInput = document.querySelector('.page-input');
204	        if (pageInput) {
205	            pageInput.value = pageNum;
