207	        
208	        // Update the report image source
209	        this.updateReportImage();
210	        
211	        // Clear selections when changing pages
212	        this.selectedViolations.clear();
213	        document.querySelectorAll('.viobox').forEach(box => box.classList.remove('active'));
214	        
215	        this.announce(`Page ${pageNum} of ${this.totalPages}`);
