225	            p.setAttribute('aria-pressed', 'false');
226	        });
227	        
228	        // Add active state to clicked pill
229	        pill.classList.add('active');
230	        pill.setAttribute('aria-pressed', 'true');
231	        
232	        // Update current bureau
233	        this.currentBureau = pill.dataset.bureau;
234	        
235	        // Update image source
236	        this.updateReportImage();
237	        
238	        this.announce(`Selected ${pill.textContent} bureau`);
