241	    navigateCreditor(direction) {
242	        // Calculate new index
243	        this.currentCreditorIndex += direction;
244	        
245	        // Wrap around
246	        if (this.currentCreditorIndex < 0) {
247	            this.currentCreditorIndex = this.creditors.length - 1;
248	        } else if (this.currentCreditorIndex >= this.creditors.length) {
249	            this.currentCreditorIndex = 0;
250	        }
251	        
252	        // Update current creditor
253	        const creditor = this.creditors[this.currentCreditorIndex];
254	        this.currentCreditor = creditor.code;
255	        
256	        // Update display
257	        const nameDisplay = document.querySelector('.creditor-name');
258	        if (nameDisplay) {
259	            nameDisplay.textContent = creditor.name;
260	        }
261	        
262	        // Update image source
263	        this.updateReportImage();
264	        
265	        this.announce(`Selected ${creditor.name}`);
266	    }
267	    
268	    updateReportImage() {
269	        const reportImage = document.querySelector('.report-image');
270	        if (reportImage) {
271	            // VioVerse filename format: {CREDITOR}-{BUREAU}-{YYYY}-{MM}-{DD}-P{PAGE}.png
272	            // Example: AL-EQ-2024-04-25-P57.png
273	            const filename = `${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${this.currentPage}.png`;
274	            reportImage.src = filename;
275	            reportImage.alt = `${this.currentCreditor} ${this.currentBureau} credit report from ${this.reportDate}, page ${this.currentPage}`;
276	        }
277	    }
278	    
279	    initializeFromImage() {
280	        const reportImage = document.querySelector('.report-image');
281	        if (reportImage && reportImage.src) {
282	            // Parse the filename: AL-EQ-2024-04-25-P57.png
283	            const filename = reportImage.src.split('/').pop().replace('.png', '');
284	            const parts = filename.split('-');
285	            
286	            if (parts.length >= 6) {
287	                // Extract components from filename
288	                const creditorCode = parts[0];
289	                const bureauCode = parts[1];
290	                const year = parts[2];
291	                const month = parts[3];
292	                const day = parts[4];
293	                const pageMatch = parts[5].match(/P(\d+)/);
294	                
295	                // Set date
296	                this.reportDate = `${year}-${month}-${day}`;
297	                const dateDisplay = document.querySelector('.date-value');
298	                if (dateDisplay) {
299	                    dateDisplay.textContent = this.reportDate;
300	                }
301	                
302	                // Set creditor
303	                const creditorIndex = this.creditors.findIndex(c => c.code === creditorCode);
304	                if (creditorIndex !== -1) {
305	                    this.currentCreditorIndex = creditorIndex;
306	                    this.currentCreditor = creditorCode;
307	                    
308	                    // Update creditor display
309	                    const nameDisplay = document.querySelector('.creditor-name');
310	                    if (nameDisplay) {
311	                        nameDisplay.textContent = this.creditors[creditorIndex].name;
312	                    }
313	                }
314	                
315	                // Set bureau and highlight correct button
316	                this.currentBureau = bureauCode;
317	                document.querySelectorAll('.bureau-pill').forEach(pill => {
318	                    const isActive = pill.dataset.bureau === bureauCode;
319	                    pill.classList.toggle('active', isActive);
320	                    pill.setAttribute('aria-pressed', isActive);
321	                });
322	                
323	                // Set page number
324	                if (pageMatch) {
325	                    this.currentPage = parseInt(pageMatch[1]);
326	                    const pageInput = document.querySelector('.page-input');
327	                    if (pageInput) {
328	                        pageInput.value = this.currentPage;
329	                    }
330	                }
331	            }
332	        }
333	    }
334	    
335	    renderViolations() {
336	        const container = document.querySelector('.viobox-container');
337	        if (!container) return;
338	        
339	        // Clear existing boxes
340	        container.innerHTML = '';
341	        
342	        // Render each violation
343	        this.violations.forEach(violation => {
344	            const viobox = this.createVIObox(violation);
345	            container.appendChild(viobox);
346	        });
347	        
348	        // Update sidebar
349	        this.updateViolationSidebar();
350	    }
351	    
352	    createVIObox(violation) {
353	        const box = document.createElement('button');
354	        box.className = 'viobox';
355	        box.setAttribute('role', 'button');
356	        box.setAttribute('aria-label', `${violation.severity} severity violation: ${violation.text}`);
357	        box.setAttribute('tabindex', '0');
358	        box.setAttribute('data-violation-id', violation.id);
359	        box.style.left = `${violation.x}px`;
360	        box.style.top = `${violation.y}px`;
361	        
362	        // Add severity icon
363	        const icon = document.createElement('div');
364	        icon.className = `severity-icon severity-${violation.severity}`;
365	        
366	        const iconEl = document.createElement('i');
367	        const iconMap = {
368	            high: 'shield-x',
369	            medium: 'alert-triangle',
370	            low: 'info'
371	        };
372	        iconEl.setAttribute('data-lucide', iconMap[violation.severity]);
373	        icon.appendChild(iconEl);
374	        
375	        box.appendChild(icon);
376	        
377	        // Add tooltip for violation text
378	        if (this.tipsEnabled) {
379	            const tooltip = document.createElement('span');
380	            tooltip.className = 'viobox-tooltip';
381	            tooltip.textContent = violation.text;
382	            box.appendChild(tooltip);
383	        }
384	        
385	        // Add click handler
386	        box.addEventListener('click', (e) => {
387	            e.preventDefault();
388	            this.selectViolation(violation, e.currentTarget);
389	        });
390	        
391	        return box;
392	    }
393	    
394	    selectViolation(violation, viobox) {
395	        const isSelected = this.selectedViolations.has(violation.id);
396	        
397	        if (isSelected) {
398	            // Deselect
399	            this.selectedViolations.delete(violation.id);
400	            viobox.classList.remove('active');
401	            this.announce(`Deselected ${violation.severity} severity violation`);
402	        } else {
403	            // Select
404	            this.selectedViolations.add(violation.id);
405	            viobox.classList.add('active');
406	            this.announce(`Selected ${violation.severity} severity violation: ${violation.text}`);
407	        }
408	        
409	        // Update counter and sidebar
410	        this.updateViolationCounter();
411	        this.updateViolationSidebar();
412	    }
413	    
414	    updateViolationSidebar() {
415	        // Update statistics to show selected violations
416	        const selectedViolations = this.violations.filter(v => this.selectedViolations.has(v.id));
417	        const stats = {
418	            high: selectedViolations.filter(v => v.severity === 'high').length,
419	            medium: selectedViolations.filter(v => v.severity === 'medium').length,
420	            low: selectedViolations.filter(v => v.severity === 'low').length
421	        };
422	        
423	        document.querySelectorAll('.stat-value').forEach((el, index) => {
424	            const severity = ['high', 'medium', 'low'][index];
425	            if (severity && stats[severity] !== undefined) {
426	                el.textContent = stats[severity];
427	            }
428	        });
429	        
430	        // Update violation list
431	        const listContainer = document.querySelector('.violations');
432	        if (listContainer) {
433	            listContainer.innerHTML = '';
434	            
435	            this.violations.forEach(violation => {
436	                const li = document.createElement('li');
437	                li.className = 'violation-item';
438	                li.innerHTML = `
439	                    <div class="violation-item-icon severity-icon severity-${violation.severity}">
440	                        <i data-lucide="${violation.severity === 'high' ? 'shield-x' : 
441	                                          violation.severity === 'medium' ? 'alert-triangle' : 'info'}"></i>
442	                    </div>
443	                    <div class="violation-item-text">
444	                        <span class="violation-severity">${violation.severity.charAt(0).toUpperCase() + violation.severity.slice(1)}</span>
445	                        <span class="violation-description">${violation.text}</span>
446	                    </div>
447	                `;
448	                listContainer.appendChild(li);
449	            });
450	            
451	            // Re-initialize Lucide icons and restore selected state
452	            lucide.createIcons();
453	            
454	            // Restore selected state for VIOboxes
455	            this.selectedViolations.forEach(id => {
456	                const box = document.querySelector(`[data-violation-id="${id}"]`);
457	                if (box) box.classList.add('active');
458	            });
459	        }
460	    }
461	    
462	    updateViolationCounter() {
463	        const counter = document.querySelector('.violation-counter .count');
464	        if (counter) {
465	            // Show number of selected violations
466	            counter.textContent = this.selectedViolations.size;
467	        }
468	    }
469	    
470	    handleKeyboard(e) {
471	        // Arrow key navigation
472	        if (e.key === 'ArrowLeft' && !e.target.matches('input')) {
473	            e.preventDefault();
474	            if (e.shiftKey) {
475	                this.navigateCreditor(-1);
476	            } else {
477	                this.navigatePage(-1);
478	            }
479	        } else if (e.key === 'ArrowRight' && !e.target.matches('input')) {
480	            e.preventDefault();
481	            if (e.shiftKey) {
482	                this.navigateCreditor(1);
483	            } else {
484	                this.navigatePage(1);
485	            }
486	        }
487	        
488	        // View switching with number keys
489	        if (e.key === '1' && e.ctrlKey) {
490	            e.preventDefault();
491	            this.switchView('report');
492	        } else if (e.key === '2' && e.ctrlKey) {
493	            e.preventDefault();
494	            this.switchView('tagger');
495	        } else if (e.key === '3' && e.ctrlKey) {
496	            e.preventDefault();
497	            this.switchView('evidence');
498	        }
499	    }
500	    
501	    announce(message) {
502	        // Create or update live region for screen reader announcements
503	        let liveRegion = document.getElementById('vioverse-live-region');
504	        if (!liveRegion) {
505	            liveRegion = document.createElement('div');
506	            liveRegion.id = 'vioverse-live-region';
507	            liveRegion.setAttribute('aria-live', 'polite');
508	            liveRegion.setAttribute('aria-atomic', 'true');
509	            liveRegion.style.position = 'absolute';
510	            liveRegion.style.left = '-10000px';
511	            liveRegion.style.width = '1px';
512	            liveRegion.style.height = '1px';
513	            liveRegion.style.overflow = 'hidden';
514	            document.body.appendChild(liveRegion);
515	        }
516	        
517	        liveRegion.textContent = message;
518	    }
519	}
520	
521	// Initialize the application
522	const vioverse = new VioVerse();
