  3	/**
  4	 * VioVerse Main Application
  5	 * Modular JavaScript for FCRA violation analysis platform
  6	 */
  7	
  8	class VioVerse {
  9	    constructor() {
 10	        this.currentView = 'report';
 11	        this.currentPage = 57;
 12	        this.totalPages = 999;
 13	        this.tipsEnabled = false;
 14	        this.docketExpanded = true;
 15	        this.selectedViolations = new Set();
 16	        this.currentCreditor = 'AL';
 17	        this.currentBureau = 'EQ';  // Default bureau
 18	        this.reportDate = '2024-04-25';  // Default date
 19	        
 20	        // Navigation data will be loaded from JSON
 21	        this.navigationData = null;
 22	        this.loadNavigationData();
 23	        
 24	        // Creditor mapping
 25	        this.creditors = [
 26	            { code: 'AL', name: 'Ally Financial' },
 27	            { code: 'BB', name: 'Citibank / Best Buy' },
 28	            { code: 'BK', name: 'Bank of America' },
 29	            { code: 'BY', name: 'Barclays Bank Delaware' },
 30	            { code: 'C1', name: 'Citizens Bank (Account 1)' },
 31	            { code: 'C2', name: 'Citizens Bank (Account 2)' },
 32	            { code: 'CR', name: 'Cornerstone Community FCU' },
 33	            { code: 'DB', name: 'Discover Bank' },
 34	            { code: 'DL', name: 'Discover Personal Loans' },
 35	            { code: 'HD', name: 'THD / Citibank (Home Depot)' },
 36	            { code: 'JP', name: 'JPMCB Card Services (Chase)' },
 37	            { code: 'MF', name: 'Mariner Finance' },
 38	            { code: 'SR', name: 'Citibank / Sears' }
 39	        ];
 40	        this.currentCreditorIndex = 0;  // Start with Ally
 41	        
 42	        // Violations will be loaded from CSV or localStorage
 43	        this.violations = [];
 44	        this.violationsData = null;
 45	        
 46	        this.init();
 47	    }
 48	    
 49	    init() {
 50	        // Wait for DOM to be ready
 51	        if (document.readyState === 'loading') {
 52	            document.addEventListener('DOMContentLoaded', () => this.setupEventListeners());
 53	        } else {
 54	            this.setupEventListeners();
 55	        }
 56	    }
 57	    
 58	    setupEventListeners() {
 59	        // View toggle buttons
 60	        const viewButtons = document.querySelectorAll('.view-btn');
 61	        viewButtons.forEach(btn => {
 62	            btn.addEventListener('click', (e) => this.switchView(e.currentTarget.dataset.view));
 63	        });
 64	        
 65	        // Docket toggle
 66	        const docketToggle = document.querySelector('.docket-toggle');
 67	        if (docketToggle) {
 68	            docketToggle.addEventListener('click', () => this.toggleDocket());
 69	        }
 70	        
 71	        // Tips toggle
 72	        const tipsToggle = document.querySelector('.tips-toggle');
 73	        if (tipsToggle) {
 74	            tipsToggle.addEventListener('click', () => this.toggleTips());
 75	        }
 76	        
 77	        // Page navigation
 78	        const prevBtn = document.querySelector('.nav-arrow.prev');
 79	        const nextBtn = document.querySelector('.nav-arrow.next');
 80	        const pageInput = document.querySelector('.page-input');
 81	        
 82	        if (prevBtn) prevBtn.addEventListener('click', () => this.navigatePage(-1));
 83	        if (nextBtn) nextBtn.addEventListener('click', () => this.navigatePage(1));
 84	        if (pageInput) {
 85	            pageInput.addEventListener('change', (e) => this.goToPage(parseInt(e.target.value)));
 86	            pageInput.addEventListener('keypress', (e) => {
 87	                if (e.key === 'Enter') {
 88	                    e.target.blur();
 89	                }
 90	            });
 91	        }
 92	        
 93	        // Bureau pills
 94	        const bureauPills = document.querySelectorAll('.bureau-pill');
 95	        bureauPills.forEach(pill => {
 96	            pill.addEventListener('click', (e) => this.selectBureau(e.currentTarget));
 97	        });
 98	        
 99	        // Creditor navigation
100	        const prevCreditor = document.querySelector('.creditor-arrow.prev');
101	        const nextCreditor = document.querySelector('.creditor-arrow.next');
102	        if (prevCreditor) prevCreditor.addEventListener('click', () => this.navigateCreditor(-1));
103	        if (nextCreditor) nextCreditor.addEventListener('click', () => this.navigateCreditor(1));
104	        
105	        // Report navigation bar handlers
106	        this.setupReportNavigation();
107	        
108	        // Initialize based on current image
109	        this.initializeFromImage();
110	        
111	        // Load violations data
112	        this.loadViolations().then(() => {
113	            // Initialize VIOboxes
114	            this.renderViolations();
115	            
116	            // Initialize counter and sidebar
117	            this.updateViolationCounter();
118	            this.updateViolationSidebar();
119	        });
120	        
121	        // Keyboard navigation
122	        document.addEventListener('keydown', (e) => this.handleKeyboard(e));
123	    }
124	    
125	    switchView(viewName) {
126	        if (viewName === this.currentView) return;
127	        
128	        // Update view state
129	        this.currentView = viewName;
130	        document.querySelector('.vioverse-app').dataset.view = viewName;
131	        
132	        // Update button states
133	        document.querySelectorAll('.view-btn').forEach(btn => {
134	            const isActive = btn.dataset.view === viewName;
135	            btn.classList.toggle('active', isActive);
136	            btn.setAttribute('aria-selected', isActive);
137	        });
138	        
139	        // Update panel visibility
140	        document.querySelectorAll('.view-panel').forEach(panel => {
141	            const isActive = panel.id === `${viewName}-view`;
142	            panel.classList.toggle('active', isActive);
143	            panel.hidden = !isActive;
144	        });
145	        
146	        // Announce view change to screen readers
147	        this.announce(`Switched to ${viewName} view`);
148	    }
