/* ========================================
   Docket Header
   ======================================== */
.docket-header {
    display: flex;
    background-color: var(--bg-card);
    border-bottom: 1px solid var(--border-subtle);
    padding: 16px 24px;
    justify-content: space-between;
    align-items: center;
    transition: all var(--transition-normal);
    position: relative;
    z-index: var(--z-base);
    box-shadow: var(--shadow-sm);
    overflow: hidden;
    max-height: 100px;
}

.docket-header.collapsed {
    max-height: 0;
    padding: 0 24px;
    border-bottom: none;
}

.docket-content {
    display: flex;
    gap: 32px;
    flex-wrap: wrap;
}

.case-info, .court-info, .judge-info, .deadline-info {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.case-label, .court-label, .judge-label, .deadline-label {
    font-size: 16px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    font-weight: var(--font-light);
}

.case-value, .court-value, .judge-value, .deadline-value {
    font-size: 18px;
    font-weight: var(--font-medium);
}

.docket-toggle {
    position: fixed;
    top: 10px;
    right: 24px;
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--color-primary);
    color: var(--text-white);
    border-radius: 24px;
    transition: all var(--transition-fast);
    box-shadow: var(--shadow-md);
    z-index: calc(var(--z-base) + 1);
}

.docket-toggle:hover {
    background-color: var(--color-primary-dark);
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

.docket-header.collapsed + .main-nav .docket-toggle {
    background-color: var(--bg-accent);
    color: var(--text-primary);
}

/* ========================================
   Main Navigation
   ======================================== */

