  7	
  8	class VioVerse {
  9	    constructor() {
 10	        this.currentView = 'report';
 11	        this.currentPage = 57;
 12	        this.totalPages = 999;
 13	        this.tipsEnabled = false;
 14	        this.docketExpanded = true;
 15	        this.selectedViolations = new Set();
 16	        this.currentCreditor = 'AL';
 17	        this.currentBureau = 'EQ';  // Default bureau
 18	        this.reportDate = '2024-04-25';  // Default date
 19	        
 20	        // Navigation data will be loaded from JSON
 21	        this.navigationData = null;
 22	        this.loadNavigationData();
 23	        
 24	        // Creditor mapping
 25	        this.creditors = [
 26	            { code: 'AL', name: 'Ally Financial' },
 27	            { code: 'BB', name: 'Citibank / Best Buy' },
 28	            { code: 'BK', name: 'Bank of America' },
 29	            { code: 'BY', name: 'Barclays Bank Delaware' },
 30	            { code: 'C1', name: 'Citizens Bank (Account 1)' },
 31	            { code: 'C2', name: 'Citizens Bank (Account 2)' },
 32	            { code: 'CR', name: 'Cornerstone Community FCU' },
 33	            { code: 'DB', name: 'Discover Bank' },
 34	            { code: 'DL', name: 'Discover Personal Loans' },
 35	            { code: 'HD', name: 'THD / Citibank (Home Depot)' },
 36	            { code: 'JP', name: 'JPMCB Card Services (Chase)' },
 37	            { code: 'MF', name: 'Mariner Finance' },
 38	            { code: 'SR', name: 'Citibank / Sears' }
 39	        ];
 40	        this.currentCreditorIndex = 0;  // Start with Ally
 41	        
 42	        // Violations will be loaded from CSV or localStorage
 43	        this.violations = [];
 44	        this.violationsData = null;
 45	        
 46	        this.init();
 47	    }
 48	    
 49	    init() {
 50	        // Wait for DOM to be ready
 51	        if (document.readyState === 'loading') {
 52	            document.addEventListener('DOMContentLoaded', () => this.setupEventListeners());
 53	        } else {
 54	            this.setupEventListeners();
 55	        }
 56	    }
 57	    
 58	    setupEventListeners() {
 59	        // View toggle buttons
 60	        const viewButtons = document.querySelectorAll('.view-btn');
 61	        viewButtons.forEach(btn => {
 62	            btn.addEventListener('click', (e) => this.switchView(e.currentTarget.dataset.view));
 63	        });
 64	        
 65	        // Docket toggle functionality
 66	        const docketToggle = document.querySelector('.docket-toggle');
 67	        const arrowCollapsed = document.querySelector('.arrow-collapsed');
 68	        if (docketToggle) {
 69	            docketToggle.addEventListener('click', () => this.toggleDocket());
 70	        }
 71	        if (arrowCollapsed) {
 72	            arrowCollapsed.addEventListener('click', () => this.toggleDocket());
 73	        }
 74	        
 75	        // Tips toggle
 76	        const tipsToggle = document.querySelector('.tips-toggle');
 77	        if (tipsToggle) {
 78	            tipsToggle.addEventListener('click', () => this.toggleTips());
 79	        }
 80	        
 81	        // Page navigation
 82	        const prevBtn = document.querySelector('.nav-arrow.prev');
 83	        const nextBtn = document.querySelector('.nav-arrow.next');
 84	        const pageInput = document.querySelector('.page-input');
 85	        
 86	        if (prevBtn) prevBtn.addEventListener('click', () => this.navigatePage(-1));
 87	        if (nextBtn) nextBtn.addEventListener('click', () => this.navigatePage(1));
 88	        if (pageInput) {
 89	            pageInput.addEventListener('change', (e) => this.goToPage(parseInt(e.target.value)));
 90	            pageInput.addEventListener('keypress', (e) => {
 91	                if (e.key === 'Enter') {
 92	                    e.target.blur();
 93	                }
 94	            });
 95	        }
 96	        
 97	        // Bureau pills
 98	        const bureauPills = document.querySelectorAll('.bureau-pill');
 99	        bureauPills.forEach(pill => {
100	            pill.addEventListener('click', (e) => this.selectBureau(e.currentTarget));
101	        });
102	        
103	        // Creditor navigation
104	        const prevCreditor = document.querySelector('.creditor-arrow.prev');
105	        const nextCreditor = document.querySelector('.creditor-arrow.next');
106	        if (prevCreditor) prevCreditor.addEventListener('click', () => this.navigateCreditor(-1));
107	        if (nextCreditor) nextCreditor.addEventListener('click', () => this.navigateCreditor(1));
108	        
109	        // Report navigation bar handlers
110	        this.setupReportNavigation();
111	        
112	        // Initialize based on current image
113	        this.initializeFromImage();
114	        
115	        // Load violations data
116	        this.loadViolations().then(() => {
117	            // Initialize VIOboxes
118	            this.renderViolations();
119	            
120	            // Initialize counter and sidebar
121	            this.updateViolationCounter();
122	            this.updateViolationSidebar();
123	        });
124	        
125	        // Keyboard navigation
126	        document.addEventListener('keydown', (e) => this.handleKeyboard(e));
127	    }
128	    
129	    switchView(viewName) {
130	        if (viewName === this.currentView) return;
131	        
132	        // Update view state
133	        this.currentView = viewName;
134	        document.querySelector('.vioverse-app').dataset.view = viewName;
135	        
136	        // Update button states
137	        document.querySelectorAll('.view-btn').forEach(btn => {
138	            const isActive = btn.dataset.view === viewName;
139	            btn.classList.toggle('active', isActive);
140	            btn.setAttribute('aria-selected', isActive);
141	        });
142	        
143	        // Update panel visibility
144	        document.querySelectorAll('.view-panel').forEach(panel => {
145	            const isActive = panel.id === `${viewName}-view`;
146	            panel.classList.toggle('active', isActive);
147	            panel.hidden = !isActive;
148	        });
149	        
150	        // Announce view change to screen readers
151	        this.announce(`Switched to ${viewName} view`);
152	    }
153	    
154	    toggleDocket() {
155	        this.docketExpanded = !this.docketExpanded;
156	        const header = document.querySelector('.docket-header');
157	        const arrowCollapsed = document.querySelector('.arrow-collapsed');
158	        const upToggle = document.querySelector('.docket-toggle');
159	        
160	        if (header && arrowCollapsed) {
161	            if (this.docketExpanded) {
162	                // Expand
163	                header.classList.remove('collapsed');
164	                arrowCollapsed.classList.remove('visible');
165	                upToggle.setAttribute('aria-expanded', 'true');
166	            } else {
167	                // Collapse
168	                header.classList.add('collapsed');
169	                arrowCollapsed.classList.add('visible');
170	                upToggle.setAttribute('aria-expanded', 'false');
171	            }
172	            
173	            // Update icons using Lucide
174	            lucide.createIcons();
175	        }
176	    }
177	    
178	    toggleTips() {
179	        this.tipsEnabled = !this.tipsEnabled;
180	        const toggle = document.querySelector('.tips-toggle');
181	        
182	        if (toggle) {
183	            toggle.setAttribute('aria-pressed', this.tipsEnabled);
184	            document.body.classList.toggle('tooltip-mode', this.tipsEnabled);
185	            
186	            // Update button text
187	            const span = toggle.querySelector('span');
188	            if (span) {
189	                span.textContent = this.tipsEnabled ? 'Tips ON' : 'Tips OFF';
190	            }
191	            
192	            // Update icon style
193	            const icon = toggle.querySelector('i');
194	            if (icon) {
195	                icon.style.fill = this.tipsEnabled ? 'currentColor' : 'none';
196	            }
197	        }
198	        
199	        this.announce(`Tooltips ${this.tipsEnabled ? 'enabled' : 'disabled'}`);
200	    }
201	    
202	    navigatePage(direction) {
203	        const newPage = this.currentPage + direction;
204	        if (newPage >= 1 && newPage <= this.totalPages) {
205	            this.goToPage(newPage);
206	        }
207	    }
208	    
209	    goToPage(pageNum) {
210	        if (pageNum < 1 || pageNum > this.totalPages || isNaN(pageNum)) {
211	            return;
212	        }
213	        
214	        this.currentPage = pageNum;
215	        const pageInput = document.querySelector('.page-input');
216	        if (pageInput) {
217	            pageInput.value = pageNum;
218	        }
219	        
220	        // Update the report image source
221	        this.updateReportImage();
222	        
223	        // Clear selections when changing pages
224	        this.selectedViolations.clear();
225	        document.querySelectorAll('.viobox').forEach(box => box.classList.remove('active'));
226	        
227	        this.announce(`Page ${pageNum} of ${this.totalPages}`);
228	        
229	        // Update violation counter
230	        this.updateViolationCounter();
231	    }
232	    
233	    selectBureau(pill) {
234	        // Remove active state from all pills
235	        document.querySelectorAll('.bureau-pill').forEach(p => {
236	            p.classList.remove('active');
237	            p.setAttribute('aria-pressed', 'false');
238	        });
239	        
240	        // Add active state to clicked pill
241	        pill.classList.add('active');
242	        pill.setAttribute('aria-pressed', 'true');
243	        
244	        // Update current bureau
245	        this.currentBureau = pill.dataset.bureau;
246	        
247	        // Update image source
248	        this.updateReportImage();
249	        
250	        this.announce(`Selected ${pill.textContent} bureau`);
251	    }
252	    
253	    navigateCreditor(direction) {
254	        // Calculate new index
255	        this.currentCreditorIndex += direction;
256	        
257	        // Wrap around
258	        if (this.currentCreditorIndex < 0) {
259	            this.currentCreditorIndex = this.creditors.length - 1;
260	        } else if (this.currentCreditorIndex >= this.creditors.length) {
261	            this.currentCreditorIndex = 0;
262	        }
263	        
264	        // Update current creditor
265	        const creditor = this.creditors[this.currentCreditorIndex];
266	        this.currentCreditor = creditor.code;
267	        
268	        // Update display
269	        const nameDisplay = document.querySelector('.creditor-name');
270	        if (nameDisplay) {
271	            nameDisplay.textContent = creditor.name;
272	        }
273	        
274	        // Update image source
275	        this.updateReportImage();
276	        
277	        this.announce(`Selected ${creditor.name}`);
278	    }
279	    
280	    updateReportImage() {
281	        const reportImage = document.querySelector('.report-image');
282	        if (reportImage) {
283	            // VioVerse filename format: {CREDITOR}-{BUREAU}-{YYYY}-{MM}-{DD}-P{PAGE}.png
284	            // Example: AL-EQ-2024-04-25-P57.png
285	            const paddedPage = String(this.currentPage).padStart(2, '0');
286	            const filename = `assets/reports/${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${paddedPage}.png`;
287	            reportImage.src = filename;
288	            
289	            // Get creditor name for alt text
290	            const creditorName = this.navigationData ? this.navigationData.creditors[this.currentCreditor] : this.currentCreditor;
291	            const bureauName = this.navigationData ? this.navigationData.bureaus[this.currentBureau] : this.currentBureau;
292	            reportImage.alt = `${creditorName} ${bureauName} credit report from ${this.reportDate}, page ${this.currentPage}`;
293	        }
294	        
295	        // Update violations for the new page
296	        this.renderViolations();
297	        this.updateViolationCounter();
298	    }
299	    
300	    initializeFromImage() {
301	        const reportImage = document.querySelector('.report-image');
302	        if (reportImage && reportImage.src) {
303	            // Parse the filename: AL-EQ-2024-04-25-P57.png
304	            const filename = reportImage.src.split('/').pop().replace('.png', '');
305	            const parts = filename.split('-');
306	            
307	            if (parts.length >= 6) {
308	                // Extract components from filename
309	                const creditorCode = parts[0];
310	                const bureauCode = parts[1];
311	                const year = parts[2];
312	                const month = parts[3];
313	                const day = parts[4];
314	                const pageMatch = parts[5].match(/P(\d+)/);
315	                
316	                // Set date
317	                this.reportDate = `${year}-${month}-${day}`;
318	                const dateDisplay = document.querySelector('.date-value');
319	                if (dateDisplay) {
320	                    dateDisplay.textContent = this.reportDate;
321	                }
322	                
323	                // Set creditor
324	                const creditorIndex = this.creditors.findIndex(c => c.code === creditorCode);
325	                if (creditorIndex !== -1) {
326	                    this.currentCreditorIndex = creditorIndex;
327	                    this.currentCreditor = creditorCode;
328	                    
329	                    // Update creditor display
330	                    const nameDisplay = document.querySelector('.creditor-name');
331	                    if (nameDisplay) {
332	                        nameDisplay.textContent = this.creditors[creditorIndex].name;
333	                    }
334	                }
335	                
336	                // Set bureau and highlight correct button
337	                this.currentBureau = bureauCode;
338	                document.querySelectorAll('.bureau-pill').forEach(pill => {
339	                    const isActive = pill.dataset.bureau === bureauCode;
340	                    pill.classList.toggle('active', isActive);
341	                    pill.setAttribute('aria-pressed', isActive);
342	                });
343	                
344	                // Set page number
345	                if (pageMatch) {
346	                    this.currentPage = parseInt(pageMatch[1]);
347	                    const pageInput = document.querySelector('.page-input');
348	                    if (pageInput) {
349	                        pageInput.value = this.currentPage;
350	                    }
351	                }
352	            }
353	        }
354	    }
355	    
356	    renderViolations() {
357	        const container = document.querySelector('.viobox-container');
358	        if (!container) return;
359	        
360	        // Clear existing boxes
361	        container.innerHTML = '';
362	        
363	        // Get violations for current page
364	        const currentPageKey = `${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${String(this.currentPage).padStart(2, '0')}`;
365	        
366	        if (this.violationsData && this.violationsData.byPage && this.violationsData.byPage[currentPageKey]) {
367	            this.violations = this.violationsData.byPage[currentPageKey];
368	        } else if (this.violationsData && this.violationsData.violations) {
369	            // Filter violations for current page from flat list
370	            this.violations = this.violationsData.violations.filter(v => 
371	                v.creditor === this.currentCreditor &&
372	                v.bureau === this.currentBureau &&
373	                v.date === this.reportDate &&
374	                v.page === this.currentPage
375	            );
376	        } else {
377	            this.violations = [];
378	        }
379	        
380	        // Render each violation
381	        this.violations.forEach(violation => {
382	            const viobox = this.createVIObox(violation);
383	            container.appendChild(viobox);
384	        });
385	        
386	        // Update sidebar
387	        this.updateViolationSidebar();
388	    }
389	    
390	    createVIObox(violation) {
391	        const box = document.createElement('button');
392	        box.className = 'viobox';
393	        box.setAttribute('role', 'button');
394	        box.setAttribute('aria-label', `${violation.severity} severity violation: ${violation.description || violation.text || ''}`);
395	        box.setAttribute('tabindex', '0');
396	        box.setAttribute('data-violation-id', violation.id);
397	        box.style.left = `${violation.x}px`;
398	        box.style.top = `${violation.y}px`;
399	        
400	        // Set custom width/height if provided
401	        if (violation.width) {
402	            box.style.width = `${violation.width}px`;
403	        }
404	        if (violation.height) {
405	            box.style.height = `${violation.height}px`;
406	        }
407	        
408	        // Add severity icon
409	        const icon = document.createElement('div');
410	        icon.className = `severity-icon severity-${violation.severity}`;
411	        
412	        const iconEl = document.createElement('i');
413	        const iconMap = {
414	            high: 'shield-x',
415	            medium: 'alert-triangle',
416	            low: 'info'
417	        };
418	        iconEl.setAttribute('data-lucide', iconMap[violation.severity]);
419	        icon.appendChild(iconEl);
420	        
421	        box.appendChild(icon);
422	        
423	        // Add tooltip for violation text
424	        if (this.tipsEnabled) {
425	            const tooltip = document.createElement('span');
426	            tooltip.className = 'viobox-tooltip';
427	            tooltip.textContent = violation.text;
428	            box.appendChild(tooltip);
429	        }
430	        
431	        // Add click handler
432	        box.addEventListener('click', (e) => {
433	            e.preventDefault();
434	            this.selectViolation(violation, e.currentTarget);
435	        });
436	        
437	        return box;
438	    }
439	    
440	    selectViolation(violation, viobox) {
441	        const isSelected = this.selectedViolations.has(violation.id);
442	        
443	        if (isSelected) {
444	            // Deselect
445	            this.selectedViolations.delete(violation.id);
446	            viobox.classList.remove('active');
447	            this.announce(`Deselected ${violation.severity} severity violation`);
448	        } else {
449	            // Select
450	            this.selectedViolations.add(violation.id);
451	            viobox.classList.add('active');
452	            this.announce(`Selected ${violation.severity} severity violation: ${violation.text}`);
453	        }
454	        
455	        // Update counter and sidebar
456	        this.updateViolationCounter();
457	        this.updateViolationSidebar();
458	    }
459	    
460	    updateViolationSidebar() {
461	        // Update statistics to show selected violations
462	        const selectedViolations = this.violations.filter(v => this.selectedViolations.has(v.id));
463	        const stats = {
464	            high: selectedViolations.filter(v => v.severity === 'high').length,
465	            medium: selectedViolations.filter(v => v.severity === 'medium').length,
466	            low: selectedViolations.filter(v => v.severity === 'low').length
467	        };
468	        
469	        document.querySelectorAll('.stat-value').forEach((el, index) => {
470	            const severity = ['high', 'medium', 'low'][index];
471	            if (severity && stats[severity] !== undefined) {
472	                el.textContent = stats[severity];
473	            }
474	        });
475	        
476	        // Update violation list
477	        const listContainer = document.querySelector('.violations');
478	        if (listContainer) {
479	            listContainer.innerHTML = '';
480	            
481	            this.violations.forEach(violation => {
482	                const li = document.createElement('li');
483	                li.className = 'violation-item';
484	                li.innerHTML = `
485	                    <div class="violation-item-icon severity-icon severity-${violation.severity}">
486	                        <i data-lucide="${violation.severity === 'high' ? 'shield-x' : 
487	                                          violation.severity === 'medium' ? 'alert-triangle' : 'info'}"></i>
488	                    </div>
489	                    <div class="violation-item-text">
490	                        <span class="violation-severity">${violation.severity.charAt(0).toUpperCase() + violation.severity.slice(1)}</span>
491	                        <span class="violation-description">${violation.text}</span>
492	                    </div>
493	                `;
494	                listContainer.appendChild(li);
495	            });
496	            
497	            // Re-initialize Lucide icons and restore selected state
498	            lucide.createIcons();
499	            
500	            // Restore selected state for VIOboxes
501	            this.selectedViolations.forEach(id => {
502	                const box = document.querySelector(`[data-violation-id="${id}"]`);
503	                if (box) box.classList.add('active');
504	            });
505	        }
506	    }
507	    
508	    updateViolationCounter() {
509	        const counter = document.querySelector('.violation-counter .count');
510	        if (counter) {
511	            // Show number of selected violations
512	            counter.textContent = this.selectedViolations.size;
513	        }
514	    }
515	    
516	    handleKeyboard(e) {
517	        // Arrow key navigation
518	        if (e.key === 'ArrowLeft' && !e.target.matches('input')) {
519	            e.preventDefault();
520	            if (e.shiftKey) {
521	                this.navigateCreditor(-1);
522	            } else {
