528	                this.navigateCreditor(1);
529	            } else {
530	                this.navigatePage(1);
531	            }
532	        }
533	        
534	        // View switching with number keys
535	        if (e.key === '1' && e.ctrlKey) {
536	            e.preventDefault();
537	            this.switchView('report');
538	        } else if (e.key === '2' && e.ctrlKey) {
539	            e.preventDefault();
540	            this.switchView('tagger');
541	        } else if (e.key === '3' && e.ctrlKey) {
542	            e.preventDefault();
