/* Creditor Display */
.creditor-display {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 8px 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
}

.creditor-info {
    display: flex;
    align-items: center;
    gap: 8px;
}

.creditor-label {
    font-size: 16px;
    color: var(--text-secondary);
    font-weight: var(--font-medium);
}

.creditor-name {
    font-size: 20px;
    font-weight: var(--font-bold);
    color: var(--color-primary);
    min-width: 200px;
}

.report-date {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 0 16px;
    border-left: 1px solid var(--border-subtle);
    border-right: 1px solid var(--border-subtle);
}

.date-label {
    font-size: 16px;
    color: var(--text-secondary);
    font-weight: var(--font-medium);
}

.date-value {
    font-size: 18px;
    font-weight: var(--font-medium);
    color: var(--text-primary);
    font-family: var(--font-mono);
}

.creditor-nav {
    display: flex;
    gap: 4px;
}

.creditor-arrow {
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-secondary);
    background-color: var(--bg-card);
    border-radius: 16px;
    transition: all var(--transition-fast);
    border: 1px solid var(--border-subtle);
}

.creditor-arrow:hover {
    background-color: var(--color-primary);
    color: var(--text-white);
    transform: translateY(-2px);
}

.creditor-arrow i {
    width: 16px;
    height: 16px;
}

