  1	// VioVerse Refactor JS â€“ initialize component behavior here
  2	
  3	/**
  4	 * VioVerse Main Application
  5	 * Modular JavaScript for FCRA violation analysis platform
  6	 */
  7	
  8	class VioVerse {
  9	    constructor() {
 10	        this.currentView = 'report';
 11	        this.currentPage = 57;
 12	        this.totalPages = 999;
 13	        this.tipsEnabled = false;
 14	        this.docketExpanded = true;
 15	        this.selectedViolations = new Set();
 16	        this.currentCreditor = 'AL';
 17	        this.currentBureau = 'EQ';  // Default bureau
 18	        this.reportDate = '2024-04-25';  // Default date
 19	        
 20	        // Navigation data will be loaded from JSON
 21	        this.navigationData = null;
 22	        this.loadNavigationData();
 23	        
 24	        // Creditor mapping
 25	        this.creditors = [
 26	            { code: 'AL', name: 'Ally Financial' },
 27	            { code: 'BB', name: 'Citibank / Best Buy' },
 28	            { code: 'BK', name: 'Bank of America' },
 29	            { code: 'BY', name: 'Barclays Bank Delaware' },
 30	            { code: 'C1', name: 'Citizens Bank (Account 1)' },
 31	            { code: 'C2', name: 'Citizens Bank (Account 2)' },
 32	            { code: 'CR', name: 'Cornerstone Community FCU' },
 33	            { code: 'DB', name: 'Discover Bank' },
 34	            { code: 'DL', name: 'Discover Personal Loans' },
 35	            { code: 'HD', name: 'THD / Citibank (Home Depot)' },
 36	            { code: 'JP', name: 'JPMCB Card Services (Chase)' },
 37	            { code: 'MF', name: 'Mariner Finance' },
 38	            { code: 'SR', name: 'Citibank / Sears' }
 39	        ];
 40	        this.currentCreditorIndex = 0;  // Start with Ally
 41	        
 42	        // Violations will be loaded from CSV or localStorage
 43	        this.violations = [];
 44	        this.violationsData = null;
 45	        
 46	        this.init();
 47	    }
 48	    
 49	    init() {
 50	        // Wait for DOM to be ready
 51	        if (document.readyState === 'loading') {
 52	            document.addEventListener('DOMContentLoaded', () => this.setupEventListeners());
 53	        } else {
 54	            this.setupEventListeners();
 55	        }
 56	    }
 57	    
 58	    setupEventListeners() {
 59	        // Legacy event listeners removed â€” see legacy-removal.html
 60	        
 61	        // New view toggle functionality
 62	        const viewLabels = document.querySelectorAll('.view-label');
 63	        viewLabels.forEach(label => {
 64	            label.addEventListener('click', (e) => this.switchView(e.currentTarget.dataset.view));
 65	        });
 66	        
 67	        // Docket toggle functionality
 68	        const docketToggle = document.querySelector('.docket-toggle');
 69	        const arrowCollapsed = document.querySelector('.arrow-collapsed');
 70	        if (docketToggle) {
 71	            docketToggle.addEventListener('click', () => this.toggleDocket());
 72	        }
 73	        if (arrowCollapsed) {
 74	            arrowCollapsed.addEventListener('click', () => this.toggleDocket());
 75	        }
 76	        
 77	        // Tool Tips toggle
 78	        const toolTipsToggle = document.querySelector('.tool-tips-toggle');
 79	        if (toolTipsToggle) {
 80	            toolTipsToggle.addEventListener('click', () => this.toggleToolTips());
 81	        }
 82	        
 83	        // Page navigation
 84	        const prevBtn = document.querySelector('.nav-arrow.prev');
 85	        const nextBtn = document.querySelector('.nav-arrow.next');
 86	        const pageInput = document.querySelector('.page-input');
 87	        
 88	        if (prevBtn) prevBtn.addEventListener('click', () => this.navigatePage(-1));
 89	        if (nextBtn) nextBtn.addEventListener('click', () => this.navigatePage(1));
 90	        if (pageInput) {
 91	            pageInput.addEventListener('change', (e) => this.goToPage(parseInt(e.target.value)));
 92	            pageInput.addEventListener('keypress', (e) => {
 93	                if (e.key === 'Enter') {
 94	                    e.target.blur();
 95	                }
 96	            });
 97	        }
 98	        
 99	        // Bureau pills
100	        const bureauPills = document.querySelectorAll('.bureau-pill');
101	        bureauPills.forEach(pill => {
102	            pill.addEventListener('click', (e) => this.selectBureau(e.currentTarget));
103	        });
104	        
105	        // Creditor navigation
106	        const prevCreditor = document.querySelector('.creditor-arrow.prev');
107	        const nextCreditor = document.querySelector('.creditor-arrow.next');
108	        if (prevCreditor) prevCreditor.addEventListener('click', () => this.navigateCreditor(-1));
109	        if (nextCreditor) nextCreditor.addEventListener('click', () => this.navigateCreditor(1));
110	        
111	        // Report navigation bar handlers
112	        this.setupReportNavigation();
113	        
114	        // Initialize based on current image
115	        this.initializeFromImage();
116	        
117	        // Load violations data
118	        this.loadViolations().then(() => {
119	            // Initialize VIOboxes
120	            this.renderViolations();
121	            
122	            // Initialize counter and sidebar
123	            this.updateViolationCounter();
124	            this.updateViolationSidebar();
125	        });
126	        
127	        // Keyboard navigation
128	        document.addEventListener('keydown', (e) => this.handleKeyboard(e));
129	        
130	        // Violation filter handlers
131	        const filterRadios = document.querySelectorAll('input[name="violation-filter"]');
132	        filterRadios.forEach(radio => {
133	            radio.addEventListener('change', () => this.updateViolationSidebar());
134	        });
135	    }
136	    
137	    switchView(viewName) {
138	        if (viewName === this.currentView) return;
139	        
140	        // Map 'tagger' to 'viotagger' for indicator positioning
141	        const indicatorClass = viewName === 'tagger' ? 'tagger' : viewName;
142	        
143	        // Update view state
144	        this.currentView = viewName;
145	        document.querySelector('.vioverse-app').dataset.view = viewName;
146	        
147	        // Legacy button state updates removed â€” see legacy-removal.html
148	        
149	        // Update new toggle
150	        const indicator = document.getElementById('toggle-indicator');
151	        if (indicator) {
152	            indicator.className = 'toggle-indicator ' + indicatorClass;
153	        }
154	        
155	        // Update new toggle label states
156	        document.querySelectorAll('.view-label').forEach(label => {
157	            const isActive = label.dataset.view === viewName;
158	            label.classList.toggle('active', isActive);
159	            label.setAttribute('aria-selected', isActive);
160	            label.setAttribute('tabindex', isActive ? '0' : '-1');
161	        });
162	        
163	        // Update panel visibility
164	        document.querySelectorAll('.view-panel').forEach(panel => {
165	            const isActive = panel.id === `${viewName}-view`;
166	            panel.classList.toggle('active', isActive);
167	            panel.hidden = !isActive;
168	        });
169	        
170	        // Announce view change to screen readers
171	        this.announce(`Switched to ${viewName} view`);
172	    }
173	    
174	    toggleDocket() {
175	        this.docketExpanded = !this.docketExpanded;
176	        const header = document.querySelector('.docket-header');
177	        const arrowCollapsed = document.querySelector('.arrow-collapsed');
178	        const upToggle = document.querySelector('.docket-toggle');
179	        
180	        if (header && arrowCollapsed) {
181	            if (this.docketExpanded) {
182	                // Expand
183	                header.classList.remove('collapsed');
184	                arrowCollapsed.classList.remove('visible');
185	                upToggle.setAttribute('aria-expanded', 'true');
186	            } else {
187	                // Collapse
188	                header.classList.add('collapsed');
189	                arrowCollapsed.classList.add('visible');
190	                upToggle.setAttribute('aria-expanded', 'false');
191	            }
192	            
193	            // Update icons using Lucide
194	            lucide.createIcons();
195	        }
196	    }
197	    
198	    toggleToolTips() {
199	        this.tipsEnabled = !this.tipsEnabled;
200	        const toggle = document.querySelector('.tool-tips-toggle');
201	        
202	        if (toggle) {
203	            toggle.setAttribute('aria-checked', this.tipsEnabled);
204	            document.body.classList.toggle('tooltip-mode', this.tipsEnabled);
205	        }
206	        
207	        this.announce(`Tooltips ${this.tipsEnabled ? 'enabled' : 'disabled'}`);
208	    }
209	    
210	    navigatePage(direction) {
211	        const newPage = this.currentPage + direction;
212	        if (newPage >= 1 && newPage <= this.totalPages) {
213	            this.goToPage(newPage);
214	        }
215	    }
216	    
217	    goToPage(pageNum) {
218	        if (pageNum < 1 || pageNum > this.totalPages || isNaN(pageNum)) {
219	            return;
220	        }
221	        
222	        this.currentPage = pageNum;
223	        const pageInput = document.querySelector('.page-input');
224	        if (pageInput) {
225	            pageInput.value = pageNum;
226	        }
227	        
228	        // Update the report image source
229	        this.updateReportImage();
230	        
231	        // Don't clear selections when changing pages - preserve them across pages
232	        // This allows counting violations across multiple pages of the same report
233	        
234	        this.announce(`Page ${pageNum} of ${this.totalPages}`);
235	        
236	        // Update violation counter
237	        this.updateViolationCounter();
238	    }
239	    
240	    selectBureau(pill) {
241	        // Remove active state from all pills
242	        document.querySelectorAll('.bureau-pill').forEach(p => {
243	            p.classList.remove('active');
244	            p.setAttribute('aria-pressed', 'false');
245	        });
246	        
247	        // Add active state to clicked pill
248	        pill.classList.add('active');
249	        pill.setAttribute('aria-pressed', 'true');
250	        
251	        // Update current bureau
252	        this.currentBureau = pill.dataset.bureau;
253	        
254	        // Update image source
255	        this.updateReportImage();
256	        
257	        this.announce(`Selected ${pill.textContent} bureau`);
258	    }
259	    
260	    navigateCreditor(direction) {
261	        // Calculate new index
262	        this.currentCreditorIndex += direction;
263	        
264	        // Wrap around
265	        if (this.currentCreditorIndex < 0) {
266	            this.currentCreditorIndex = this.creditors.length - 1;
267	        } else if (this.currentCreditorIndex >= this.creditors.length) {
268	            this.currentCreditorIndex = 0;
269	        }
270	        
271	        // Update current creditor
272	        const creditor = this.creditors[this.currentCreditorIndex];
273	        this.currentCreditor = creditor.code;
274	        
275	        // Update display
276	        const nameDisplay = document.querySelector('.creditor-name');
277	        if (nameDisplay) {
278	            nameDisplay.textContent = creditor.name;
279	        }
280	        
281	        // Update image source
282	        this.updateReportImage();
283	        
284	        this.announce(`Selected ${creditor.name}`);
285	    }
286	    
287	    updateReportImage() {
288	        const reportImage = document.querySelector('.report-image');
289	        if (reportImage) {
290	            // VioVerse filename format: {CREDITOR}-{BUREAU}-{YYYY}-{MM}-{DD}-P{PAGE}.png
291	            // Example: AL-EQ-2024-04-25-P57.png
292	            const paddedPage = String(this.currentPage).padStart(2, '0');
293	            const filename = `assets/reports/${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${paddedPage}.png`;
294	            reportImage.src = filename;
295	            
296	            // Get creditor name for alt text
297	            const creditorName = this.navigationData ? this.navigationData.creditors[this.currentCreditor] : this.currentCreditor;
298	            const bureauName = this.navigationData ? this.navigationData.bureaus[this.currentBureau] : this.currentBureau;
299	            reportImage.alt = `${creditorName} ${bureauName} credit report from ${this.reportDate}, page ${this.currentPage}`;
300	        }
301	        
302	        // Update violations for the new page
303	        this.renderViolations();
304	        this.updateViolationCounter();
305	    }
306	    
307	    initializeFromImage() {
308	        const reportImage = document.querySelector('.report-image');
309	        if (reportImage && reportImage.src) {
310	            // Parse the filename: AL-EQ-2024-04-25-P57.png
311	            const filename = reportImage.src.split('/').pop().replace('.png', '');
312	            const parts = filename.split('-');
313	            
314	            if (parts.length >= 6) {
315	                // Extract components from filename
316	                const creditorCode = parts[0];
317	                const bureauCode = parts[1];
318	                const year = parts[2];
319	                const month = parts[3];
320	                const day = parts[4];
321	                const pageMatch = parts[5].match(/P(\d+)/);
322	                
323	                // Set date
324	                this.reportDate = `${year}-${month}-${day}`;
325	                const dateDisplay = document.querySelector('.date-value');
326	                if (dateDisplay) {
327	                    dateDisplay.textContent = this.reportDate;
328	                }
329	                
330	                // Set creditor
331	                const creditorIndex = this.creditors.findIndex(c => c.code === creditorCode);
332	                if (creditorIndex !== -1) {
333	                    this.currentCreditorIndex = creditorIndex;
334	                    this.currentCreditor = creditorCode;
335	                    
336	                    // Update creditor display
337	                    const nameDisplay = document.querySelector('.creditor-name');
338	                    if (nameDisplay) {
339	                        nameDisplay.textContent = this.creditors[creditorIndex].name;
340	                    }
341	                }
342	                
343	                // Set bureau and highlight correct button
344	                this.currentBureau = bureauCode;
345	                document.querySelectorAll('.bureau-pill').forEach(pill => {
346	                    const isActive = pill.dataset.bureau === bureauCode;
347	                    pill.classList.toggle('active', isActive);
348	                    pill.setAttribute('aria-pressed', isActive);
349	                });
350	                
351	                // Set page number
352	                if (pageMatch) {
353	                    this.currentPage = parseInt(pageMatch[1]);
354	                    const pageInput = document.querySelector('.page-input');
355	                    if (pageInput) {
356	                        pageInput.value = this.currentPage;
357	                    }
358	                }
359	            }
360	        }
361	    }
362	    
363	    renderViolations() {
364	        const container = document.querySelector('.viobox-container');
365	        if (!container) return;
366	        
367	        // Clear existing boxes
368	        container.innerHTML = '';
369	        
370	        // Get violations for current page
371	        const currentPageKey = `${this.currentCreditor}-${this.currentBureau}-${this.reportDate}-P${String(this.currentPage).padStart(2, '0')}`;
372	        
373	        if (this.violationsData && this.violationsData.byPage && this.violationsData.byPage[currentPageKey]) {
374	            this.violations = this.violationsData.byPage[currentPageKey];
375	        } else if (this.violationsData && this.violationsData.violations) {
376	            // Filter violations for current page from flat list
377	            this.violations = this.violationsData.violations.filter(v => 
378	                v.creditor === this.currentCreditor &&
379	                v.bureau === this.currentBureau &&
380	                v.date === this.reportDate &&
381	                v.page === this.currentPage
382	            );
383	        } else {
384	            this.violations = [];
385	        }
386	        
387	        // Render each violation
388	        this.violations.forEach(violation => {
389	            const viobox = this.createVIObox(violation);
390	            container.appendChild(viobox);
391	        });
392	        
393	        // Update sidebar
394	        this.updateViolationSidebar();
395	    }
396	    
397	    createVIObox(violation) {
398	        const box = document.createElement('button');
399	        box.className = 'viobox';
400	        box.setAttribute('role', 'button');
401	        box.setAttribute('aria-label', `${violation.severity} severity violation: ${violation.description || violation.text || ''}`);
402	        box.setAttribute('tabindex', '0');
403	        box.setAttribute('data-violation-id', violation.id);
404	        box.style.left = `${violation.x}px`;
405	        box.style.top = `${violation.y}px`;
406	        
407	        // Set custom width/height if provided
408	        if (violation.width) {
409	            box.style.width = `${violation.width}px`;
410	        }
411	        if (violation.height) {
412	            box.style.height = `${violation.height}px`;
413	        }
414	        
415	        // Add severity icon
416	        const icon = document.createElement('div');
417	        icon.className = `severity-icon severity-${violation.severity}`;
418	        
419	        const iconEl = document.createElement('i');
420	        const iconMap = {
421	            high: 'shield-x',
422	            medium: 'alert-triangle',
423	            low: 'info'
424	        };
425	        iconEl.setAttribute('data-lucide', iconMap[violation.severity]);
426	        icon.appendChild(iconEl);
427	        
428	        box.appendChild(icon);
429	        
430	        // Add tooltip for violation text
431	        if (this.tipsEnabled) {
432	            const tooltip = document.createElement('span');
433	            tooltip.className = 'viobox-tooltip';
434	            tooltip.textContent = violation.text;
435	            box.appendChild(tooltip);
436	        }
437	        
438	        // Add click handler
439	        box.addEventListener('click', (e) => {
440	            e.preventDefault();
441	            this.selectViolation(violation, e.currentTarget);
442	        });
443	        
444	        return box;
445	    }
446	    
447	    selectViolation(violation, viobox) {
448	        const isSelected = this.selectedViolations.has(violation.id);
449	        
450	        if (isSelected) {
451	            // Deselect
452	            this.selectedViolations.delete(violation.id);
453	            viobox.classList.remove('active');
454	            this.announce(`Deselected ${violation.severity} severity violation`);
455	        } else {
456	            // Select
457	            this.selectedViolations.add(violation.id);
458	            viobox.classList.add('active');
