470	        
471	        let violationsToCount = [];
472	        let stats = { high: 0, medium: 0, low: 0 };
473	        
474	        switch (selectedFilter) {
475	            case 'total':
476	                // Get ALL violations for current creditor/bureau/date (across all pages)
477	                violationsToCount = this.getAllViolationsForCurrentReport();
478	                break;
479	                
480	            case 'selected':
481	                // Get only selected violations across all pages
482	                violationsToCount = this.getSelectedViolationsForCurrentReport();
483	                break;
484	                
485	            case 'page':
486	                // Get only violations on current page
487	                violationsToCount = this.violations;
488	                break;
