491	        // Count violations by severity
492	        stats = {
493	            high: violationsToCount.filter(v => v.severity === 'high').length,
494	            medium: violationsToCount.filter(v => v.severity === 'medium').length,
495	            low: violationsToCount.filter(v => v.severity === 'low').length
