554	    handleKeyboard(e) {
555	        // Arrow key navigation for new toggle
556	        if (document.activeElement.classList.contains('view-label')) {
557	            const labels = Array.from(document.querySelectorAll('.view-label'));
558	            const currentIndex = labels.indexOf(document.activeElement);
559	            
560	            if (e.key === 'ArrowLeft' && currentIndex > 0) {
561	                e.preventDefault();
562	                labels[currentIndex - 1].focus();
563	            } else if (e.key === 'ArrowRight' && currentIndex < labels.length - 1) {
564	                e.preventDefault();
565	                labels[currentIndex + 1].focus();
566	            }
567	        }
568	        // Arrow key navigation
569	        if (e.key === 'ArrowLeft' && !e.target.matches('input')) {
570	            e.preventDefault();
571	            if (e.shiftKey) {
572	                this.navigateCreditor(-1);
573	            } else {
574	                this.navigatePage(-1);
575	            }
576	        } else if (e.key === 'ArrowRight' && !e.target.matches('input')) {
577	            e.preventDefault();
578	            if (e.shiftKey) {
579	                this.navigateCreditor(1);
580	            } else {
581	                this.navigatePage(1);
582	            }
583	        }
584	        
585	        // View switching with number keys
586	        if (e.key === '1' && e.ctrlKey) {
587	            e.preventDefault();
588	            this.switchView('report');
589	        } else if (e.key === '2' && e.ctrlKey) {
590	            e.preventDefault();
591	            this.switchView('tagger');
592	        } else if (e.key === '3' && e.ctrlKey) {
593	            e.preventDefault();
