693	    navigateBureau(direction) {
694	        if (!this.navigationData) return;
695	        
696	        const bureauCodes = Object.keys(this.navigationData.bureaus);
697	        const currentIndex = bureauCodes.indexOf(this.currentBureau);
698	        let newIndex = currentIndex + direction;
699	        
700	        // Check if we need to advance date
701	        if (newIndex >= bureauCodes.length) {
702	            // Moving forward past last bureau - advance date
703	            this.navigateDate(1);
704	            this.currentBureau = bureauCodes[0]; // First bureau
705	            this.updateBureauDisplay();
706	            this.findAvailableCreditor();
707	            this.highlightTransition('date');
708	            return;
709	        } else if (newIndex < 0) {
710	            // Moving backward past first bureau - go to previous date
711	            this.navigateDate(-1);
712	            this.currentBureau = bureauCodes[bureauCodes.length - 1]; // Last bureau
713	            this.updateBureauDisplay();
714	            // Find last creditor for this bureau/date
715	            const bureauReports = this.navigationData.reports[this.currentBureau];
716	            if (bureauReports && bureauReports[this.reportDate]) {
717	                const creditors = Object.keys(bureauReports[this.reportDate]);
718	                if (creditors.length > 0) {
719	                    this.currentCreditor = creditors[creditors.length - 1];
720	                    this.updateCreditorDisplay();
721	                    this.findAvailablePage();
722	                }
723	            }
724	            this.highlightTransition('date');
725	            return;
726	        }
727	        
728	        this.currentBureau = bureauCodes[newIndex];
729	        this.updateBureauDisplay();
730	        this.findAvailableReport();
731	    }
