737	        const bureauReports = this.navigationData.reports[this.currentBureau];
738	        if (!bureauReports) return;
739	        
740	        const dates = Object.keys(bureauReports).sort();
741	        const currentIndex = dates.indexOf(this.reportDate);
742	        let newIndex = currentIndex + direction;
743	        
744	        // Wrap around
745	        if (newIndex < 0) newIndex = dates.length - 1;
746	        if (newIndex >= dates.length) newIndex = 0;
747	        
748	        this.reportDate = dates[newIndex];
749	        this.updateDateDisplay();
750	        this.findAvailableCreditor();
751	    }
