756	        // Get creditors for current bureau and date
757	        const bureauReports = this.navigationData.reports[this.currentBureau];
758	        if (!bureauReports || !bureauReports[this.reportDate]) return;
759	        
760	        const availableCreditors = Object.keys(bureauReports[this.reportDate]);
761	        const currentIndex = availableCreditors.indexOf(this.currentCreditor);
762	        let newIndex = currentIndex + direction;
763	        
764	        // Check if we need to advance to next/previous bureau
765	        if (newIndex >= availableCreditors.length) {
766	            // Moving forward past last creditor - advance bureau
767	            this.navigateBureau(1);
768	            this.highlightTransition('bureau');
769	            return;
770	        } else if (newIndex < 0) {
771	            // Moving backward past first creditor - go to previous bureau
772	            this.navigateBureau(-1);
773	            // Set to last creditor of the new bureau
774	            const newBureauReports = this.navigationData.reports[this.currentBureau];
775	            if (newBureauReports && newBureauReports[this.reportDate]) {
776	                const newCreditors = Object.keys(newBureauReports[this.reportDate]);
