949	        if (!tooltip) return;
950	        
951	        // Determine what the next destination would be
952	        const bureauReports = this.navigationData.reports[this.currentBureau];
953	        if (!bureauReports || !bureauReports[this.reportDate]) return;
954	        
955	        const availableCreditors = Object.keys(bureauReports[this.reportDate]);
956	        const currentIndex = availableCreditors.indexOf(this.currentCreditor);
957	        let nextIndex = currentIndex + direction;
958	        
959	        // Check if we'll cross a boundary
960	        if (nextIndex >= availableCreditors.length) {
961	            // Will advance to next bureau
962	            const bureauCodes = Object.keys(this.navigationData.bureaus);
963	            const currentBureauIndex = bureauCodes.indexOf(this.currentBureau);
964	            const nextBureauIndex = (currentBureauIndex + 1) % bureauCodes.length;
965	            const nextBureau = bureauCodes[nextBureauIndex];
966	            
967	            if (nextBureauIndex === 0) {
968	                // Will also advance date
