971	                tooltip.textContent = `next: ally â€“ ${this.navigationData.bureaus[nextBureau].toLowerCase()}`;
972	            }
973	        } else if (nextIndex < 0) {
974	            // Will go to previous bureau
975	            const bureauCodes = Object.keys(this.navigationData.bureaus);
976	            const currentBureauIndex = bureauCodes.indexOf(this.currentBureau);
977	            const prevBureauIndex = currentBureauIndex - 1 < 0 ? bureauCodes.length - 1 : currentBureauIndex - 1;
978	            const prevBureau = bureauCodes[prevBureauIndex];
