    color: #9ba1a6;
}

.view-label.evidence {
    left: 188px; /* Moved right by 3px total */
    width: 75px;
    color: #9ba1a6;
}

/* Active state labels */
.view-label.active {
    color: #ffffff;
}

/* Focus states for accessibility */
.view-label:focus {
