   ======================================== */
.main-content {
    position: relative;
    background: transparent;
    width: 100%;
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
    padding: 40px 0;
}

.view-panel {
    width: 100%;
    display: none;
}

.view-panel.active {
    display: block;
}

.content-wrapper {
    position: relative;
    display: flex;
