    max-width: 1920px;
    margin: 0 auto;
    gap: 24px;
    padding: 0 24px;
    justify-content: center;
    align-items: flex-start;
}

/* ========================================
   Report Canvas
   ======================================== */
.report-canvas-container,
.tagger-canvas-container,
