}

.document-segment {
    position: absolute;
    left: 280px;
    width: 280px;
}

.evidence-page-segment {
    position: absolute;
    left: 640px;
    width: 62px;
}

/* Updated nav-segment base styles */
.nav-segment {
    display: flex;
    align-items: center;
    height: 17px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    z-index: 1;
    background: none; /* Ensure segments don't cover dividers */
}

/* Labels and values in same container */
.nav-text-container {
    display: flex;
    align-items: center;
    gap: 4px;
    height: 17px;
    line-height: 17px;
    white-space: nowrap;
}

.nav-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #9ba1a6;
    text-transform: lowercase;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}

.nav-value {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}

/* Creditor name specific truncation */
.creditor-segment .nav-value {
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 100px; /* Adjust based on available space before arrows */
}

/* Arrows positioned side-by-side */
.nav-arrows {
    position: absolute;
    display: flex;
    gap: 5px;
    align-items: center;
}

/* Specific arrow positioning for each segment */
.bureau-segment .nav-arrows {
    left: 81px; /* No change needed - relative to segment */
}

.date-segment .nav-arrows {
    left: 137px;
}

.creditor-segment .nav-arrows {
    left: 154px;
}

.page-segment .nav-arrows {
    left: 64px;
}

/* Evidence nav arrows positioning */
.category-segment .nav-arrows {
    right: 8px;
}

.document-segment .nav-arrows {
    right: 8px;
}

.evidence-page-segment .nav-arrows {
    left: 64px;
}

/* Updated arrow button styles */
.nav-arrow-small {
    width: 17px;
    height: 17px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
    border: none;
    color: #9ba1a6;
    transition: all var(--transition-fast);
    cursor: pointer;
    padding: 0;
    position: relative;
    z-index: 2;
}

.nav-arrow-small:hover {
    color: #f26419;
}

.nav-arrow-small i {
    width: 17px;
    height: 17px;
}

/* Vertical dividers */
.nav-divider {
    position: absolute;
    width: 1px;
    height: 18px;
    background-color: #9ba1a6;
    top: 50%;
    transform: translateY(-50%);
    z-index: 10; /* Higher than nav-bar z-index of 2 */
}

/* Report nav dividers - using specific classes for precise control */
.report-nav-bar .divider-1 {
    left: 151px; /* Spacer1: x=520px in spec, relative to nav bar at x=369px = 151px */
}

.report-nav-bar .divider-2 {
    left: 359px; /* Spacer2: x=728px in spec, relative to nav bar at x=369px = 359px */
}

.report-nav-bar .divider-3 {
    left: 583px; /* Spacer3: x=952px in spec, relative to nav bar at x=369px = 583px */
}

/* Evidence nav dividers */
.evidence-nav-bar .nav-divider:nth-of-type(1) {
    left: 260px;
}

.evidence-nav-bar .nav-divider:nth-of-type(2) {
    left: 620px;
}

/* Navigation transition highlight */
.nav-segment.nav-transitioning {
    animation: nav-highlight 0.4s ease;
}

@keyframes nav-highlight {
    0% {
        box-shadow: none;
    }
    50% {
        box-shadow: 0 0 12px rgba(242, 100, 25, 0.3);
    }
    100% {
        box-shadow: none;
    }
}

/* Respect reduced motion preference */
@media (prefers-reduced-motion: reduce) {
    .nav-segment.nav-transitioning {
        animation: none;
        box-shadow: 0 0 12px rgba(242, 100, 25, 0.3);
    }
}

/* Navigation tooltips */
.nav-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    background-color: #f26419;
    color: #ffffff;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 10px;
    text-transform: lowercase;
    padding: 6px 12px;
    border-radius: 18px;
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.2s ease, visibility 0.2s ease;
    transition-delay: 0.15s;
    pointer-events: none;
    z-index: 10000;
}

.nav-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 4px solid transparent;
    border-top-color: #f26419;
}

/* Show tooltips only when tips are enabled and on hover */
.tooltip-mode .nav-arrow-small:hover .nav-tooltip {
    opacity: 1;
    visibility: visible;
}

.report-canvas,
.tagger-canvas,
.evidence-canvas {
    width: 100%;
    height: 100%;
    position: relative;
    background-color: #ffffff;
    overflow: hidden;
    z-index: 1;
    border-radius: 24px; /* Match container border-radius */
}

/* Canvas placeholder for empty states */
.canvas-placeholder {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100%;
    color: #9ba1a6;
    font-family: 'Space Grotesk', sans-serif;
}

.canvas-placeholder h2 {
    font-size: 32px;
    font-weight: 700;
    margin-bottom: 16px;
    color: #253541;
}

.canvas-placeholder p {
    font-size: 18px;
    font-weight: 400;
    color: #9ba1a6;
}

/* Ensure all canvas containers don't shrink */
.report-canvas-container,
.tagger-canvas-container,
.evidence-canvas-container {
    flex-shrink: 0;
}

/* Ensure all sidebars have consistent width */
.violation-sidebar,
.tagger-sidebar,
.evidence-sidebar {
    width: 100%;
    max-width: 400px;
    flex-shrink: 0;
}


/* View-specific alignment to ensure consistency */
#report-view .content-wrapper,
#tagger-view .content-wrapper,
#evidence-view .content-wrapper {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    max-width: 1234px; /* 810px canvas + 24px gap + 400px sidebar */
    margin: 0 auto;
    padding: 0 24px;
}

.report-image {
    width: 100%;
    height: auto;
    max-height: 100%;
    object-fit: contain;
}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    border: 2px solid var(--viobox-inactive);
    border-radius: 13px;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    padding-left: 8px; /* Move content 8px from left */
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
    z-index: 4;
}

.viobox:hover {
    border-color: var(--viobox-active);
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border: 4px solid #253541;
    background-color: rgba(0, 102, 255, 0.1);
    box-shadow: 0 0 0 3px rgba(0, 102, 255, 0.2);
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 19.2px; /* 20% smaller: 24px * 0.8 */
    height: 19.2px; /* 20% smaller: 24px * 0.8 */
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: none;
    margin-left: -11px; /* Move 3px more left to align with border (-8px - 3px) */
}

.severity-icon i {
    width: 12.8px; /* 20% smaller: 16px * 0.8 */
    height: 12.8px; /* 20% smaller: 16px * 0.8 */
    color: #ffffff;
}

/* Dot styles - circles with no stroke */
.severity-high {
    background-color: #ff0000;
}

.severity-medium {
    background-color: #f26419;
}

.severity-low {
    background-color: #f2b919;
}

/* ========================================
   Sidebars
   ======================================== */
.violation-sidebar, .tagger-sidebar, .evidence-sidebar {
    display: block;
    width: var(--sidebar-width);
    background-color: var(--bg-card);
