    margin: 26px auto 0; /* Added 26px top margin to move down */
}

/* Report Navigation Bar */
.report-nav-bar,
.evidence-nav-bar {
    position: absolute;
    left: 9px;
    top: 15px; /* Positioned inside canvas */
    width: 792px;
    height: 40px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-shadow: none;
    overflow: visible;
    z-index: 2;
}

/* Bureau segment with specific positioning */
