}

.sidebar-heading {
    font-size: 24px;
    font-weight: var(--font-bold);
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
