    text-transform: lowercase;
    color: var(--text-secondary);
    letter-spacing: 0;
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 16px 0;
}

.violations {
