    margin-bottom: 16px;
    color: #253541;
}

.canvas-placeholder p {
    font-size: 18px;
    font-weight: 400;
    color: #9ba1a6;
}

/* Ensure all canvas containers don't shrink */
.report-canvas-container,
.tagger-canvas-container,
.evidence-canvas-container {
    flex-shrink: 0;
}

/* Ensure all sidebars have consistent width */
.violation-sidebar,
.tagger-sidebar,
.evidence-sidebar {
    width: 100%;
    max-width: 400px;
    flex-shrink: 0;
}


/* View-specific alignment to ensure consistency */
#report-view .content-wrapper,
#tagger-view .content-wrapper,
#evidence-view .content-wrapper {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    max-width: 1234px; /* 810px canvas + 24px gap + 400px sidebar */
    margin: 0 auto;
    padding: 0 24px;
}

.report-image {
    width: 100%;
    height: auto;
    max-height: 100%;
    object-fit: contain;
}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    border: 2px solid rgba(203, 213, 225, 0.9); /* viobox-inactive with 0.9 opacity */
    border-radius: 13px;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    padding-left: 8px; /* Move content 8px from left */
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
    z-index: 4;
    box-sizing: border-box; /* Include border in width/height */
}

.viobox:hover {
    border-color: rgba(0, 102, 255, 0.9); /* viobox-active with 0.9 opacity */
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border-color: #253541;
    background-color: rgba(0, 102, 255, 0.1);
    outline: 2px solid #253541;
    outline-offset: -2px;
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 19.2px; /* 20% smaller: 24px * 0.8 */
    height: 19.2px; /* 20% smaller: 24px * 0.8 */
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: none;
    margin-left: -12px; /* Move 1px more left to align with border (-11px - 1px) */
    padding: 3px; /* Add padding to shrink icon appearance */
    box-sizing: border-box; /* Include padding in width/height */
    position: relative;
    z-index: 10; /* Above VIObox borders */
}

.severity-icon i {
    width: 100%; /* Fill available space after padding */
    height: 100%; /* Fill available space after padding */
    color: #ffffff;
}

/* Dot styles - circles with no stroke */
.severity-high {
    background-color: #ff0000;
}

.severity-medium {
    background-color: #f26419;
}

.severity-low {
    background-color: #f2b919;
}

/* ========================================
   Sidebar Canvas Structure
   ======================================== */
.sidebar-canvas {
    position: fixed;
    left: 1180px;
    top: 69px;
    width: 452px;
    height: 920px;
    z-index: var(--z-base);
}

/* Bottom box (base layer) */
.sidebar-bottom-box {
    position: absolute;
    width: 452px;
    height: 920px;
    background-color: #253541;
    border: 16px solid #253541;
    border-radius: 36px;
    box-sizing: border-box;
}

/* Top box (content layer) */
.sidebar-top-box {
    position: absolute;
    left: 16px;
    top: 57px;
    width: 419px;
    height: 848px;
    background-color: #ffffff;
    border-radius: 36px;
    overflow-y: auto;
    overflow-x: hidden;
    padding: 40px 32px;
    box-sizing: border-box;
}

/* Variable heading */
.sidebar-variable-heading {
    position: absolute;
    left: 40px;
    top: 14px;
    width: 374px;
    height: 39px;
    font-family: 'Bernino Sans Compressed', sans-serif;
    font-weight: 800;
    font-size: 36pt;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
}

/* FCRA stays uppercase */
.sidebar-variable-heading .uppercase {
    text-transform: uppercase;
}

/* Old sidebar styles - to be removed after HTML update */
.violation-sidebar, .tagger-sidebar, .evidence-sidebar {
    display: none;
}

.sidebar-heading {
    font-size: 24px;
    font-weight: var(--font-bold);
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-sm);
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
    color: #ffffff;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    color: var(--text-secondary);
    letter-spacing: 0;
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

.violation-item {
    display: flex;
    align-items: flex-start;
    gap: 12px;
    padding: 12px;
    margin-bottom: 8px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    transition: all var(--transition-fast);
    border: 1px solid var(--border-subtle);
}

.violation-item:hover {
    background-color: var(--bg-hover);
    transform: translateX(4px);
    box-shadow: var(--shadow-sm);
}

.violation-item.selected {
    background-color: rgba(0, 102, 255, 0.1);
    border-color: var(--color-primary);
}

.violation-item-icon {
    flex-shrink: 0;
}

.violation-item-text {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.violation-severity {
    font-size: 14px;
    font-weight: var(--font-medium);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--text-secondary);
}

.violation-description {
    font-size: 16px;
    font-weight: var(--font-regular);
    color: var(--text-primary);
    line-height: 1.4;
}

/* ========================================
   Violation Filter Styles
   ======================================== */
.violation-filter {
    margin-bottom: 24px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

.filter-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 12px 0;
    color: var(--text-secondary);
}

.filter-options {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.filter-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 500;
    color: var(--text-primary);
}

.filter-option input[type="radio"] {
    width: 16px;
    height: 16px;
    margin: 0;
    cursor: pointer;
    accent-color: var(--color-primary);
}

.filter-option span {
    text-transform: lowercase;
}

/* ========================================
   Evidence Sidebar Styles
   ======================================== */
.evidence-summary {
    background-color: var(--bg-accent);
    border-radius: 12px;
    padding: 20px;
    margin-bottom: 24px;
    border: 1px solid var(--border-subtle);
}

.summary-item {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    margin-bottom: 12px;
}

.summary-item:last-child {
    margin-bottom: 0;
}

.summary-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: var(--text-secondary);
    text-transform: lowercase;
}

.summary-value {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: var(--text-primary);
    text-transform: lowercase;
}

.evidence-metadata {
    padding: 20px;
    background-color: var(--bg-accent);
    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-sidebar {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
        margin-top: 16px;
        height: auto;
    }

    .viobox {
        width: 90%;
        max-width: 557px;
    }
}

/* ========================================
   Tooltip System
   ======================================== */
.tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 12px;
    background-color: #f26419;
    color: var(--text-white);
    border-radius: 8px;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 10000;
    box-shadow: var(--shadow-lg);
}

.tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: #f26419;
}

.tooltip-mode .tips-toggle:hover .tooltip {
    opacity: 1;
}

/* VIObox Tooltips */
.viobox-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 16px;
    background-color: #f26419;
    color: var(--text-white);
    border-radius: 8px;
    font-size: 14px;
    font-weight: var(--font-regular);
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 10000;
    box-shadow: var(--shadow-lg);
}

.viobox-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: #f26419;
}

.tooltip-mode .viobox:hover .viobox-tooltip {
    opacity: 1;
}

