    font-family: 'Bernino Sans Compressed', sans-serif;
    font-weight: 800;
    font-size: 36pt;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
}

/* FCRA stays uppercase */
.sidebar-variable-heading .uppercase {
