292	<body>
293	    <!-- White sidebar background box -->
294	    <div class="sidebar-top-box"></div>
295	    
296	    <!-- Toggle button -->
297	    <button class="severity-toggle" aria-expanded="true" aria-controls="severity-content">
298	        <i data-lucide="circle-chevron-up"></i>
299	    </button>
300	    
301	    <!-- Severity content -->
302	    <div class="severity-container">
303	        <span class="severity-collapsed-label">show severity</span>
304	        <h2 class="severity-header">severity summary</h2>
305	        <div class="severity-content" id="severity-content">
306	            <div class="severity-stack">
307	        <!-- High Severity Box -->
308	        <div class="severity-box severity-high">
309	            <div class="severity-label">high<br>severity</div>
310	            <div class="dot-counter-group">
311	                <div class="badge-dot">
312	                    <i data-lucide="shield-x"></i>
313	                </div>
314	                <span class="severity-counter" data-severity="high">8</span>
315	            </div>
316	        </div>
317	        
318	        <!-- Medium Severity Box -->
319	        <div class="severity-box severity-medium">
320	            <div class="severity-label">medium<br>severity</div>
321	            <div class="dot-counter-group">
322	                <div class="badge-dot">
323	                    <i data-lucide="triangle-alert"></i>
324	                </div>
325	                <span class="severity-counter" data-severity="medium">12</span>
326	            </div>
327	        </div>
328	        
329	        <!-- Low Severity Box -->
330	        <div class="severity-box severity-low">
331	            <div class="severity-label">low<br>severity</div>
332	            <div class="dot-counter-group">
333	                <div class="badge-dot">
334	                    <i data-lucide="circle-alert"></i>
335	                </div>
336	                <span class="severity-counter" data-severity="low">4</span>
337	            </div>
338	        </div>
339	            </div>
340	        </div>
341	    </div>
342	    
343	    <script>
344	        // Initialize Lucide icons
345	        lucide.createIcons();
346	        
347	        // Collapsible toggle functionality
348	        const container = document.querySelector('.severity-container');
349	        const toggle = document.querySelector('.severity-toggle');
350	        const content = document.querySelector('.severity-content');
351	        const collapsedLabel = document.querySelector('.severity-collapsed-label');
352	        
353	        function toggleCollapse() {
354	            const isExpanded = toggle.getAttribute('aria-expanded') === 'true';
355	            
356	            // Toggle collapsed class
357	            container.classList.toggle('collapsed');
358	            
359	            // Update aria-expanded
360	            toggle.setAttribute('aria-expanded', !isExpanded);
361	            
362	            // Switch icon
363	            const icon = toggle.querySelector('i');
364	            if (isExpanded) {
365	                icon.setAttribute('data-lucide', 'circle-chevron-down');
366	            } else {
367	                icon.setAttribute('data-lucide', 'circle-chevron-up');
368	            }
369	            
370	            // Re-render icons
371	            lucide.createIcons();
372	            
373	            // Force resize of toggle icon in collapsed state
374	            setTimeout(() => {
375	                const toggleSvg = toggle.querySelector('svg');
376	                if (toggleSvg && container.classList.contains('collapsed')) {
377	                    toggleSvg.setAttribute('width', '12');
378	                    toggleSvg.setAttribute('height', '12');
379	                } else if (toggleSvg) {
380	                    toggleSvg.setAttribute('width', '32');
381	                    toggleSvg.setAttribute('height', '32');
382	                }
383	            }, 50);
384	            
385	            // Announce state change for accessibility
386	            const announcement = isExpanded ? 'Severity summary collapsed' : 'Severity summary expanded';
387	            const liveRegion = document.createElement('div');
388	            liveRegion.setAttribute('aria-live', 'polite');
389	            liveRegion.setAttribute('aria-atomic', 'true');
390	            liveRegion.style.position = 'absolute';
391	            liveRegion.style.left = '-9999px';
392	            liveRegion.textContent = announcement;
393	            document.body.appendChild(liveRegion);
394	            setTimeout(() => liveRegion.remove(), 1000);
395	        }
396	        
397	        // Add click event to toggle button
398	        toggle.addEventListener('click', toggleCollapse);
399	        
400	        // Add click event to collapsed label
401	        collapsedLabel.addEventListener('click', toggleCollapse);
402	        
403	        // Example of data-driven counter updates
404	        function updateSeverityCounts(counts) {
405	            ['high', 'medium', 'low'].forEach(severity => {
406	                if (counts[severity] !== undefined) {
407	                    const counter = document.querySelector(`[data-severity="${severity}"]`);
408	                    counter.textContent = counts[severity];
409	                    // Apply three-digits class for numbers >= 100
410	                    if (counts[severity] >= 100) {
411	                        counter.classList.add('three-digits');
412	                    } else {
413	                        counter.classList.remove('three-digits');
414	                    }
415	                }
416	            });
417	        }
418	        
419	        // Apply three-digits class on initial load
420	        document.querySelectorAll('.severity-counter').forEach(counter => {
421	            const value = parseInt(counter.textContent);
422	            if (value >= 100) {
423	                counter.classList.add('three-digits');
424	            }
425	        });
426	        
427	        // Example usage:
428	        // updateSeverityCounts({ high: 8, medium: 12, low: 4 });
429	    </script>
430	</body>
431	</html>

