// Severity Summary Collapse Functionality
const severityComponent = document.querySelector('.severity-summary-component');
const severityToggle = document.querySelector('.severity-summary-toggle');
const severityContent = document.querySelector('.severity-summary-content');
const severityCollapsedLabel = document.querySelector('.severity-summary-collapsed-label');

function toggleSeveritySummary() {
    const isExpanded = severityToggle.getAttribute('aria-expanded') === 'true';

    // Toggle collapsed class
    severityComponent.classList.toggle('collapsed');

    // Update aria-expanded
    severityToggle.setAttribute('aria-expanded', !isExpanded);

    // Switch icon
    const icon = severityToggle.querySelector('i');
    if (isExpanded) {
        icon.setAttribute('data-lucide', 'circle-chevron-down');
    } else {
        icon.setAttribute('data-lucide', 'circle-chevron-up');
    }

    // Re-render icons
    lucide.createIcons();

    // Force resize of toggle icon in collapsed state
    setTimeout(() => {
        const toggleSvg = severityToggle.querySelector('svg');
        if (toggleSvg && severityComponent.classList.contains('collapsed')) {
            toggleSvg.setAttribute('width', '12');
            toggleSvg.setAttribute('height', '12');
        } else if (toggleSvg) {
            toggleSvg.setAttribute('width', '32');
            toggleSvg.setAttribute('height', '32');
        }
    }, 50);
}

// Add click event to toggle button
if (severityToggle) {
    severityToggle.addEventListener('click', toggleSeveritySummary);
}

// Add click event to collapsed label
if (severityCollapsedLabel) {
    severityCollapsedLabel.addEventListener('click', toggleSeveritySummary);
}

// Apply three-digits class on initial load
document.querySelectorAll('.severity-counter').forEach(counter => {
    const value = parseInt(counter.textContent);
    if (value >= 100) {
        counter.classList.add('three-digits');
    }
});

// Example function to update severity counts dynamically
window.updateSeverityCounts = function(counts) {
    ['high', 'medium', 'low'].forEach(severity => {
        if (counts[severity] !== undefined) {
            const counter = document.querySelector(`[data-severity="${severity}"]`);
            if (counter) {
                counter.textContent = counts[severity];
                // Apply three-digits class for numbers >= 100
                if (counts[severity] >= 100) {
                    counter.classList.add('three-digits');
                } else {
                    counter.classList.remove('three-digits');
                }
            }
        }
    });
};

// Independent collapse button functionality
const customCollapseBtn = document.getElementById('custom-collapse-btn');
if (customCollapseBtn) {
    customCollapseBtn.addEventListener('click', () => {
        const isExpanded = customCollapseBtn.getAttribute('aria-expanded') === 'true';
        const icon = customCollapseBtn.querySelector('i');

        if (isExpanded) {
            // Switch to up arrow
            customCollapseBtn.setAttribute('aria-expanded', 'false');
            icon.setAttribute('data-lucide', 'circle-chevron-up');
        } else {
            // Switch to down arrow
            customCollapseBtn.setAttribute('aria-expanded', 'true');
            icon.setAttribute('data-lucide', 'circle-chevron-down');
        }

        // Re-render the icon
        lucide.createIcons();
    });
}

