file.content:
/* Add this to your main CSS file or create a new emoji-fix.css */

/* Ensure consistent emoji rendering across browsers */
.emoji-text {
  font-family: 'Apple Color Emoji', 'Segoe UI Emoji', 'Noto Color Emoji', 'Segoe UI Symbol', 'Android Emoji', 'EmojiSymbols', sans-serif;
}

/* Force emoji font rendering */
span[style*="EmojiSymbols"] {
  -webkit-font-feature-settings: "liga" off;
  font-feature-settings: "liga" off;
  text-rendering: optimizeLegibility;
}

/* Prevent emoji from being affected by text transformations */
.matters-emoji {
  text-transform: none !important;
  font-variant: normal !important;
  font-weight: normal !important;
}


/* Evidence Canvas Container */
.evidence-canvas-container {
  position: absolute;
  top: 19px;
  left: 1100px;
  width: 795px;
  height: 1042px;
  background-color: white;
  border-radius: 24px;
  z-index: 10; /* Higher z-index to ensure it's above everything */
  display: none; /* Hidden by default */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Optional: adds a subtle shadow to make it more visible */
}

#Canvas_Heading_Container {
  position: absolute;
  top: 140px;
  left: 15px;
  width: 810px;
  height: 60px;
}

#Canvas_Heading_Background {
  position: relative;
  width: 100%;
  height: 60px;
  background: #2c3338;
  border-radius: 12px;
  display: flex;
  align-items: center;
}

/* Red sections for arrows */
.arrow-section {
  position: absolute;
  top: 0;
  height: 60px;
  width: 120px;
  background: #ff0000;
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 0 15px;
}

.arrow-section.left {
  left: 0;
  border-top-left-radius: 12px;
  border-bottom-left-radius: 12px;
}

.arrow-section.right {
  right: 0;
  border-top-right-radius: 12px;
  border-bottom-right-radius: 12px;
}

#Canvas_Heading_Text {
  flex-grow: 1;
  color: white;
  font-family: "bernino-sans-compressed", sans-serif;
  font-weight: 800;
  font-size: 30px;
  text-transform: uppercase;
  text-align: center;
  margin: 0 120px;
  white-space: nowrap;
}

.canvas-arrow {
  width: 32.5px;
  height: 32px;
  display: block;
}

/* Ensure arrows maintain aspect ratio */
.canvas-arrow img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  display: block;
}

/* Remove any previous container styles */
.canvas-arrow-container {
  display: none;
}

