739	    padding: var(--content-padding);
740	    overflow-y: auto;
741	    box-shadow: -4px 0 6px rgba(0, 0, 0, 0.05);
742	}
743	
744	.sidebar-heading {
745	    font-size: 24px;
746	    font-weight: var(--font-bold);
747	    margin: 0 0 24px 0;
748	}
749	
750	/* Violation Statistics */
751	.violation-stats {
752	    display: grid;
753	    gap: 16px;
754	    margin-bottom: 32px;
755	}
756	
757	.stat-card {
758	    display: flex;
759	    align-items: center;
760	    gap: 16px;
761	    padding: 16px;
762	    background-color: var(--bg-accent);
763	    border-radius: 24px;
764	    border: 1px solid var(--border-subtle);
765	    box-shadow: var(--shadow-sm);
766	}
767	
768	.stat-icon {
769	    width: 32px;
770	    height: 32px;
771	    border-radius: 8px;
772	    display: flex;
773	    align-items: center;
774	    justify-content: center;
775	}
776	
777	.stat-icon i {
778	    width: 20px;
779	    height: 20px;
780	}
781	
782	.stat-details {
783	    display: flex;
784	    flex-direction: column;
785	}
786	
787	.stat-label {
788	    font-family: 'Space Grotesk', sans-serif;
789	    font-size: 16px;
790	    font-weight: 700;
791	    text-transform: lowercase;
792	    color: var(--text-secondary);
793	    letter-spacing: 0;
794	}
795	
796	.stat-value {
797	    font-size: 32px;
798	    font-weight: var(--font-bold);
799	    color: var(--text-primary);
800	}
801	
802	/* Violation List */
803	.violation-list {
804	    margin-top: 32px;
805	}
806	
807	.list-heading {
808	    font-family: 'Space Grotesk', sans-serif;
809	    font-size: 16px;
810	    font-weight: 700;
811	    text-transform: lowercase;
812	    margin: 0 0 16px 0;
813	}
814	
815	.violations {
816	    list-style: none;
817	    padding: 0;
818	    margin: 0;
819	}
820	
821	.violation-item {
822	    display: flex;
823	    align-items: flex-start;
824	    gap: 12px;
825	    padding: 12px;
826	    margin-bottom: 8px;
827	    background-color: var(--bg-accent);
828	    border-radius: 24px;
829	    transition: all var(--transition-fast);
830	    border: 1px solid var(--border-subtle);
831	}
832	
833	.violation-item:hover {
834	    background-color: var(--bg-hover);
835	    transform: translateX(4px);
836	    box-shadow: var(--shadow-sm);
837	}
838	
839	.violation-item-icon {
840	    flex-shrink: 0;
841	}
842	
843	.violation-item-text {
844	    display: flex;
845	    flex-direction: column;
846	    gap: 4px;
847	}
848	
849	.violation-severity {
850	    font-size: 14px;
851	    font-weight: var(--font-medium);
852	    text-transform: uppercase;
853	    letter-spacing: 0.5px;
854	    color: var(--text-secondary);
855	}
856	
857	.violation-description {
858	    font-size: 16px;
859	    font-weight: var(--font-regular);
860	    color: var(--text-primary);
861	    line-height: 1.4;
862	}
863	
864	/* ========================================
865	   Responsive Design
866	   ======================================== */
867	@media (max-width: 1024px) {
868	    .docket-content {
869	        gap: 16px;
870	    }
871	    
872	    .nav-left, .nav-center, .nav-right {
873	        gap: 16px;
874	    }
875	    
876	    .violation-sidebar {
877	        width: 320px;
878	    }
879	}
880	
881	@media (max-width: 768px) {
882	    .content-wrapper {
883	        flex-direction: column;
884	    }
885	    
886	    .violation-sidebar, .tagger-sidebar, .evidence-context {
887	        width: 100%;
888	        border-left: none;
889	        border-top: 1px solid var(--border-subtle);
890	    }
891	    
892	    .viobox {
893	        width: 90%;
894	        max-width: 557px;
895	    }
896	}
897	
898	/* ========================================
899	   Tooltip System
900	   ======================================== */
901	.tooltip {
902	    position: absolute;
903	    bottom: calc(100% + 8px);
904	    left: 50%;
905	    transform: translateX(-50%);
